css:
@-moz-document domain("roblox.com") {
body {
    background-color: #191919;
    color: #191919;
    font-family: "Source Sans Pro", Arial, Helvetica, sans-serif;
    font-weight: 300;
    font-size: 16px;
    width: 100%;
    min-width: 320px;
    margin-bottom: 210px;
    overflow-y: scroll;
}
.rbx-header {
    background-color: rgb(54, 144, 187);
}
.notification-blue {
    background-color: #bb3636;
    border-radius: 12px;
    font-size: 12px;
    font-weight: 400;
    color: #fff;
    text-align: center;
    padding: 4px 6px;
    min-width: 24px;
}
.content {
    max-width: 1338px;
    background-color: #000000;
    padding-top: 12px;
    margin: 0 auto;
}
.section-content {
    background-color: #868686;
    box-shadow: 0 0 0px 0 rgba(25, 25, 25, 0.3);
    padding: 15px;
    position: relative;
}
}
