/*@import url('https://fonts.googleapis.com/css?family=Pacifico|Open+Sans:600,700');    */



@-moz-document domain("play.spotify.com") {
    
                                /* Customize these */
  
:root {
--bg: #1d1e2a !important;          
--bg2: #1a1b26 !important;          
--bg3: #14151F !important;           
--first-color: #888 !important;            
--second-color: #74857C !important;            
--third-color: #222 !important;            
}    
  
  
                                                /* COLORS */
  
         /* header */
  
.main-menu.narrow-menu, .nav, .background, .mo-info, .fadeIn, .puff .background, .ctx, #topLists, body, html, select, .btn-info {
    background: var(--bg3) !important;
}  
  
         /* body */
  
.pf-nav > .list-group, .header, .header.playlist .header-data .main .ellipsis, .form-control, #featuredPlaylists .row, .infiniteList, div.tab-panel:nth-child(8) > div:nth-child(3) > div:nth-child(2), .container, .history .selectable, #section-settings > div {
    background: var(--bg) !important;
  }  
  
  .row {
    margin-top: 0 !important;
  }  
  
       /* navs */
  
.header-bar > div, .dropdown-interior-menu, .col-sm-6 .pf-images, .input-wrapper, .history .selectable:hover, .history .selectable.selected {
     background: var(--bg2) !important;
}    
  
       /* playlists */
  
.item-data, .pf-info, .list-group-item.list-group-item-media, .media-info {
    background: var(--bg2) !important;
  }  
  
          /* now playing */
  
#now-playing, .dropdown-interior-menu > li > a:hover, .dropdown-interior-menu > li > a:focus, .dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus, .dropdown-submenu:hover > a, .dropdown-submenu:focus > a, #desktop, .personalized-stations .button-info, .your-stations .button-info, .recommended-stations .button-info, .genre-stations .button-info {
    background: var(--bg3) !important;
  }  
  
         /* song lists */
  
  .pf-playlist-view, .tl-cell, .tl-row, .button-primary:hover {
      background: var(--bg2) !important;
  }  
  
  
                                             /* txt color */
  
  
  
  /* main */
body, html {
       color: var(--first-color) !important;
}  
  
/* link */
  a, .tl-row, .tl-highlight, .tl-row.playing .tl-cell, .tl-row.playing .tl-cell a:link, .tl-row.playing .tl-highlight, #shuffle.active, #repeat.active, #queue.active, #volume-show, #shuffle, #repeat, #queue {
    color: var(--first-color) !important;
  }  
  
  
                                    /* accent */
  
.btn-primary, #bar-inner, .main-menu.narrow-menu a.active::after, .main-menu.narrow-menu a.selected::after, .list-group-item.list-group-item-media.active::before, .nav > li.active > a::after, .button-primary {
    background: var(--second-color) !important;
  } 
  
.text-muted a:focus, .text-muted a:hover, a.text-muted:focus, a.text-muted:hover, .form-control {
    color: var(--second-color) !important;
  }  

  .tl-row.playing .tl-cell, .tl-row.playing .tl-cell a:link, .tl-row.playing .tl-highlight, #shuffle.active, #repeat.active, #queue.active {
    color: var(--second-color) !important;
  }
  
  .btn-primary {
    color: var(--third-color) !important;
  }  
  
  
                              /* font */
  
  html, body, a {
    font-family: "Open Sans" !important;
    font-weight: 600 !important;
  }  
  
                                            /* LAYOUT 
  
  body, html, body:not(.selectable) {
    overflow-x: hidden !important;
    overflow-y: hidden !important;
  }  
  
  body:hover, html:hover, body:not(.selectable):hover {
    overflow-x: auto !important;
    overflow-y: auto !important;
  }    
  */
  
.container {
    max-width: 2000px;
    padding-left: 0px !important;
    padding-right: 0px !important;
    padding-top: 50px !important;
  margin: 0 !important;
  }    
  
  h1 {
    margin-top: 70px !important;
    padding: 20px !important;
    font-family: "Pacifico" !important;
    display: none !important;
  }  
  
  #radio-app-spotify\:app\:radio-0-1483867387308 {
    margin-top: 50px !important;
  }  
  
  #main {
    left: 0 !important;
  }  
  
                        /* header */
   
.main-menu.narrow-menu {
    width: 100% !important;
    min-height: 50px !important;
    max-height: 50px !important;    
  }  
  
.nav > li:first-child {
    margin-left: 10px !important;
  }  
  
    /*
  
.nav {
  width: 100% !important;  
  padding: 5px !important;
  left: 0% !important;  
  margin: 0 !important;
  } 

 .nav, .navbar-fixed-wrapper {
    position: absolute !important;
    top: 0 !important;
  }
  */
  
  .section-divider {
    margin-top: 0px !important;
    padding: 10px !important;
  }  
  
  .nav > li.active > a::after {
    height: 5px !important;
  }
  
  
                            /* head items */
  
  
  .main-menu.narrow-menu .logo {
      background: url("http://i.imgur.com/Q9TZIDj.png") no-repeat center !important;                           
      margin-left: 20px !important;  
      opacity: 0.7 !important;
  } 
  
  
  #nav-search, #nav-browse, #nav-radio, #nav-collection, #nav-follow, .main-menu.narrow-menu a#nav-settings {
    top: -56px !important;
    border: none !important;    
  }
  
  .main-menu.narrow-menu .logo {
    max-height: 20px !important;
  }  
  
  #nav-search {
    position: absolute !important;
    left: 6% !important;
  }
  
  #nav-browse {
    position: absolute !important;
    left: 12% !important;
  }  
  
  #nav-radio {
    position: absolute !important;
    left: 19% !important;
  }  
  
  #nav-collection {
    position: absolute !important;
    left: 26% !important;
  }  
  
  #nav-follow {
    position: absolute !important;
    left: 33% !important;
  }  
  
  .item-profile, .main-menu.narrow-menu .item-profile {
    position: absolute !important;
    left: 45% !important;
    top: -19px !important;
  }  
  
#nav-settings, .main-menu.narrow-menu a#nav-settings, .main-menu.narrow-menu a#nav-settings {
    position: absolute !important;
    top: -46px !important;
    margin-left: 750px !important;
  }  
  
  .main-menu.narrow-menu a {
    display: inline-block !important;
  }  
  
  
                                 /* albums/artists pop up */
 
  
  .songs-list-container, .songs-list-container .songs-list  {
    margin-top: 50px !important;    
  }
  
  .header-artist .h-media {    
    margin: 0 auto !important;
    margin-left: 20px !important;
    margin-top: 45px !important;
  }  
 
  
#art-area, .row {
    margin-top: 50px;
  }  
  
  #wrap {
    padding: 10px !important;
  }  
  
  .media-image .media-image-clip, #widget-2 > div:nth-child(1) > a:nth-child(1) > div:nth-child(1) {
    filter: blur(3px) !important;
  }
  
  #main > div.stacked > div.front {
    width: 80% !important;
    padding: 0 !important;
  }  
  
  div.tab-panel:nth-child(8) > div:nth-child(3) > div:nth-child(2) {
    padding: 30px !important;
  }
  
  .media.genre .name span {
    font-family: "Pacifico" !important;
  }  
  
  
img, img:hover, .media-image .media-image-internal::after {
    transition: ease-in-out .5s !important;
  }  
  
                               /* playlist label */
  
.header-bar > div {
     opacity: 1 !important;  
}  
  
  
                                    /* left sidebar */
  
  .main-menu.narrow-menu a .nav-text {
    line-height: 0 !important;
  }  
  
  .main-menu.narrow-menu a .nav-text:hover {
    line-height: 1 !important;
  }   
  
                                    /* playing bar */
  
  #now-playing {
    margin-top: 0px !important;
  }  
  
                                 /* search */
  
#suggest-area {
    margin-left: -200px !important;
  }
      
  
#suggest-area.show {
    height: 53% !important;
    width: 25% !important;
    max-width: 400px !important;
    top: 150px !important;
    left: 800px !important;
  }  
  
.s-scroller {
    height: 800px !important;
  }  
  
 
  
                                                   /* add shadows */
  
.main-menu.narrow-menu {
    box-shadow: 2px 2px 7px 3px rgba(0,0,0,0.0) !important;
  }  
  
  .nav {
    box-shadow: 2px 2px 7px 3px rgba(0,0,0,0.0) !important;
  }  
  
                                       /* REMOVE SHIT */
  
  
  
  
                    /* remove seperators */

.tl-cell, .tl-header th, .header-bar > div, .nav, #now-playing-widgets, .etched-top, .h-bar, .btn-primary, .btn, #main > div.stacked > div.front iframe, .section-divider, .form-control, hr, .header-image, .button-primary:hover, .list-group-item {
    border: none !important;
}  
  
  .button-primary, .button-add:hover, .added .button-add, .button-add.added {
    border: 0px solid #888 !important;
  }  

                      /* remove useless shit, ads borders etc */
  
  #desktop .mft, #now-playing-widgets::after, .etched-top::before, .main-menu.narrow-menu a.active::after, .section-divider, .spoticon-plus-32::before {
    display: none !important;
  }  
  
  
#core {
   margin-top: -20px !important;
}
  
                              /* remove underline */
  
a, a:hover {
  border: none !important;
  transition: ease-in-out .3s !important;
  }  
  
 
                            /* remove shadows */
  #suggest-area, .dropdown-menu > li > a, .dropdown-interior-menu > li > a {
    box-shadow: none !important;
  }  
  
  
  
  #suggest-area, .s-scroller > .s-pane > .s-slider, .mo-info {
    background: transparent !important;
  }
  
  
}
