css:
@-moz-document domain("iu.zid.tuwien.ac.at") {
/* variables */
html {
    --background-color: rgb(67, 72, 88);
    --window-color: rgb(77, 82, 98);
    --shadow-color: rgb(57, 62, 78);
    --text-color: rgb(225, 225, 225);
    --textWhite-color: rgb(255, 255, 255);
    --header-color: #393e4e;
    --headerBorder-color: #252525;
}

body {
    background-color: var(--background-color);
}

#header img[src="https://idp.zid.tuwien.ac.at/fileadmin/zid/images/zid/zidheader2010.jpg"]{
    display: none !important;
    height: 92px !important;
}
#tulogo img, #logo img{
    mix-blend-mode: multiply;
}

#main-content {
    color: var(--text-color);
    background: var(--background-color);
}

#main-content h1 {
    color: var(--text-color);
}

#main-content a {
    color: #75a7bf;
}

#header {
    background: var(--header-color);   
}

#footer {
    background-color: var(--window-color);
    color: var(--text-color);
}
#footer a {
    color: #75a7bf;
}

a {
    color: #75a7bf;
}

.highlighted {
    background-color: var(--window-color);
    border: 1px solid var(--header-color) !important;
    color: #989ca0;
}

#breadcrumbs {
    border-top: 6px solid var(--background-color);
}

#topbar2col {
    background: var(--window-color);
}

hr {
   border: 1px solid var(--header-color) !important; 
}
input[type="reset"], input[type="submit"]{
    border: 1px solid var(--background-color) !important;
    border-radius: 3px;
    background-color: var(--header-color);
    text-shadow: none;
    color: gray;
    padding: 4px 8px 4px;
    margin-bottom: 8px;
    font-size: 14px;
}
input[type="reset"]:hover, input[type="submit"]:hover {
    background-color: #4f5464;
    color: var(--text-color)}

input[name="name"], input[name="pw"], input[name="totp"]{
    background-color: var(--header-color);
    border: 1px solid var(--background-color) !important;
    border-radius: 2px;
    color: var(--text-color);    
    padding: 5px 8px 5px;
}

#side-bar ul.line {
    border-bottom: 1px dotted var(--window-color);
}

fieldset {
    border: 1px solid var(--header-color);
}


table.contenttable td, th {
    border: 1px solid var(--header-color);
}

th {
    background: var(--window-color);
    color: var(--text-color);
}

#side-bar {
    background: none;
    background-color: var(--header-color);
}
#side-bar a{
    color: var(--text-color)}
#container2col {
    background-color: var(--header-color);
    background: var(--header-color) repeat-y;
}

#side-bar ul.image {
    border-bottom: 1px dotted var(--window-color);
}

tr[style="background-color:#e8e8e8"] {
    background: var(--header-color) !important;
}
}
