css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pythonworld.ru") {
  body {background: #333; color: #999}
  .widget_search input#s {background: #000;}
  .entry-header h1, .entry-header h2 {color: #DBDBDB;}
  a {color: #010139;}
  .hentry, .widget {background-color: #333;}
  .widget-title {color: #bbb;}
  .copybutton, pre {background: #030303;}
  .go {color: #8cd268;}
  .note {background: #1c382a;}
  input[type="email"]{background-color: #888;}
  .main-navigation ul {background-color: #014259;}
  .main-navigation ul li:focus ul a, .main-navigation ul li:hover ul a {background: #074054;color: #fff;}
}
