css:
@-moz-document domain(www.tumblr.com) {

/*background*/
#left_column, #right_column, .l-content, .post.post_full, .post .post-source-footer, .post_full.is_conversation .conversation_lines .chat_line:nth-child(even), .radar .radar_controls, .layout_standard {
    background:linear-gradient(#FFF, #F9F9F9, #DDD)!important;
   /*background:linear-gradient(#79D0ED, #799EED, #7F79ED)!important;*/
    background-attachment:fixed!important;
}

/*header*/
.l-header-container--refresh,
.post-forms-glass {
  background-color: #FBFBFB!important;
}

.new_post_label i {
    color: #118dff!important;
}


/* postun altı ikonlar yazılar mavi */
.post_controls .post_control.reblog:before,
.post_controls .post_control.reply:before,
.post_controls .post_control.share:before,
.post_controls .post_control.like:before,
.post_full .post_notes_label .note_link_current,
.post_controls .post_control.post_control_menu:before,
.post_controls .post_control.no_icon,
.post_controls .post_control.no_icon.show_label,
.post_full .post_tags .post_tag,
.post .post-source-footer .post-source-name-prefix,
.post .post-source-footer a.post-source-link,
.reblog_follow_button .follow-text,
.worded-follow-button .follow-text,
.post .post_info .reblog_icon:before,
.icon_plus:before, .radar .radar_controls .note_count>a, .radar .radar_button{
    color: #118dff;
}

/*text*/
.identity .controls_section.user_list li .follow_list_item_blog .tumblelog_title,
.identity .controls_section.user_list li .follow_list_item_blog .tumblelog_name,
.sidebar_link, .section_header, .identity .controls_section.follow_list.recommended_tumblelogs>li, .identity .controls_section.follow_list.trending_tumblelogs>li, .identity .controls_section li.section_header, .radar .radar_tumblelog_label .name, .radar .radar_tumblelog_label .subheading, .new_post_label_text, .post_full .post_header .post_info .post_info_link:first-child, .hide_overflow, .small_text, .identity .right_column .small_text{
    color: #333;
}

.identity .controls_section li.section_header {
    border-bottom: 2px solid #118dff;
}
.identity .controls_section.follow_list.recommended_tumblelogs>li.item, .reblog-list-item {
    border-bottom: 1px solid #118dff;
}
.identity .controls_section li {
border-top: 1px solid #118dff;
}
.l-header-container--refresh,
.post-forms-glass {
    border-bottom: 3.3px solid #118dff;
}
.tab_bar .tab.active,
.tab_bar .tab.active .tab_anchor,
.tab_bar .tab.selected,
.tab_bar .tab.selected .tab_anchor,
.tab_bar .tab_anchor,
.selected .tab_notice {
    color: #118dff;
    opacity: 1;
}
#posts.posts>.post_container {
    border: 1px solid #118dff;
    border-radius: 3px;
}
.post.new_post .post_buttons {
    padding: 0 1px 0 5px;
}
#posts.posts {
    list-style-type: none;
    margin: 0 0 0 83px;
    padding: 0;
}

.compose-button {
    fill: #118dff;
}

.post .dock_video_button {
    background-color: #118dff;
}
/*disable avatar loop*/

.post_avatar.post-avatar--fixed {
    position: absolute !important;
    top: 0 !important;
    left: -85px !important;
}
.post_avatar.post-avatar--absolute {
    position: absolute;
    top: 0 !important;
    left: -85px !important;
    bottom: inherit !important;
}
.post_avatar.post-avatar--sticky .avatar-wrapper {
    position: absolute !important;
    top: 0px !important;
    height: auto;
    width: auto;}
.post_avatar.post-avatar--sticky {
    height: 64px !important;}

/*hide dashboard button*/
.iframe-controls-container a.dashboard-button,
#iframe_controls a.btn.icon.dashboard {
    display: none !important;}

/*hide embed button*/
.iframe-controls-container a.tx-button[href$="embed"],
.iframe_controls a.btn.embed {
    display: none !important;}

/* disable dashboard adds */
.notification.single_notification.alt.takeover-container.mb_tracked,
.remnant-unit-container,
.remnant_ad,
.sponsored_post,
.stretchy_kids,
.takeover-container .sponsored-day-media-section,
.video-ad,
.yamplus-unit-container {
    display: none!important}


/*disable install theme button on your theme page */
#NST[title="Tumblr Themes"], .btn.icon.follow[title="Theme Maker"],
a[href^="http://themecloud.co/themes"][style*="fixed"],
a.install[href^="http://www.tumblr.com/theme/"],
a[title*="Tumblr Themes"][href^="http://www.dolliecrave.com"],
a.btn[href="http://www.themesltd.com/tumblr/tumblr-themes"],
#credz, .install_button > a, #install_theme_button, a#install-btn,
div[onclick*="themecloset.me/themes"] {display: none !important;}
.standalone-ad-container {display: none;}

/*logo*/
H1.logo {background-size: contain;background-image: url(http://i.hizliresim.com/GP4Pjr.png);}/*blue logo*/
div.preload-container {display: none;}/*logo no-repeat*/
.logo .png-logo {background: url(http://i.hizliresim.com/ZMzZq0.png) 22px 11px no-repeat;}/*drop logo*/


#new-xkit-control > a{
background: url(http://i.hizliresim.com/0lpWQW.png) 50% 50% no-repeat !important;
background-size: 23px 23px !important;
padding-top: 2px;
}

.sidebar_link {
display:none;
}


.post_avatar, .identity .controls_section.user_list li .follow_list_item_blog, .identity .controls_section.user_list li .follow_list_item_blog:before,
.radar .radar_footer .radar_avatar:before, .identity .right_column:after, .right_column li:hover, .post .post_tags.draggable .post_tags_inner {
    background: transparent!important;
}
.post_avatar .post_avatar_link {
    background-color: transparent;
}
.follow_list_item_blog:before {
    border-right: 0!important;
}
.identity .controls_section .controls_section_item {
    border-color: #dadada;
}
.post_full .post_permalink {
    border-color: #F7F7F7 #F7F7F7 transparent transparent;
}

/*dashboard notice*/
#user_tools .tab .tab_notice,
#user_tools .tab .tab-notice--outlined {
    color: rgba(25, 25, 25, 0.9)!important;
    background-color: #118dff;
    border-color: #118dff;
}
.tab_notice .tab_notice_value {
    color: #fff;
}
.ui_search.active .search_form_field:before,
.ui_search.selected .search_form_field:before {
    color: #118dff;
}
#user_tools .tab .tab_notice,
#user_tools .tab .tab-notice--outlined {
    color: #118dff;
}

/*image contrast*/

img {
    -webkit-filter: contrast(120%) brightness(110%);  /* Safari */
    filter: contrast(115%) brightness(95%);
}


/*expanded chat*/
.notification .notification_sentence .username, .notification .notification_sentence .banner_text .notification .notification_sentence a {
    color: #118dff;}

.messaging-conversation-popovers .messaging-conversations-container.messaging-open-conversations {
    right: 10px;
    bottom: 0;
    height: calc(100% - 89px);
    min-height: 250px;
    max-height: 550px;}
.conversation-message-error {
	display:none;}
.messaging-conversation-popovers .messaging-open-conversations .popover--conversation-popover {
    position: relative;
    width: 350px;
    height: 100%;}
.messaging-conversation--open .tx-scroll .tx-scroll-container>div {
    width: 340px;
    box-sizing: border-box;}
.messaging-conversation--open .conversation-message-text .message-bubble-header .name, .messaging-conversation--open .conversation-message-text .message-bubble-header .retry-button {
    margin-bottom: 0px;}
.tx-scroll .tx-scrollbar-thumb {
    background: #ccc;}
.tx-scroll .tx-scrollbar-thumb:hover {
    background: #ccc;}
tx-scroll .tx-scrollbar-thumb:active,.tx-scroll .tx-scrollbar-track--active .tx-scrollbar-thumb {
    background: #ccc;}
.conversation-compose-plugin--stickers, .conversation-compose-plugin--gif-search {
	display:none;}
.messaging-conversation--open .conversation-compose .conversation-compose-plugins-container {
    padding-left: 3px;}


@media screen and (max-width: 700px) {

}


}/*dont delete this. this is moz document bracket*/
