@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sqlite.org") {
    /*[[fixed-width-enabled]]*/ body {
        max-width: 45em;
        margin-left: auto;
        margin-right: auto;
    }
    body {
        background-color: #002b36;
        color: #839496;
    }
    h1, h2, h3, h4, h5, h6,
    .fancy h1, .fancy h2, .fancy h3, .fancy h4, .fancy h5, .fancy h6,
    .fancy_title {
        color: #b58900;
    }
    a, a:visited,
    .fancy_toc > a, #toc_sub a {
        color: #268bd2;
    }
    .fancy :link:hover, .fancy :visited:hover {
        background: none !important;
    }
    blockquote,
    .fancy .codeblock {
        padding: 1em;
        border: 1px solid #839496;
        border-radius: 1em;
        background-color: #073642;
    }
    .fancy .codeblock i {
        color: #859900;
    }
    blockquote > pre {
        margin: 0px;
    }
    pre {
        font-family: monospace;
    }
    .yyterm {
        background-color: #002b36;
        border-color: #839496;
        color: #fdf6e3;
        padding: 0.1em 0.5em;
        font-family: monospace;
    }
    .showhide > li > a {
        color: #93a1a1;
    }
}
