  }
  
  /* End unibet */
  
  
  /* end sidebar */
  
  
  
  /* Stats */
  a *{
    color: #ccc;
  }
  #box-stats tr.section_title th, #box-stats .title{
     border: 1px solid #CCC;
  }
  #box-stats{
    background-color: #222;
    border: 1px solid #CCC;
    
  }
  #box-stats .line_2 td{
    background-color: #333;
  }
  #box-stats td.sort-by{
    background-color: #333;
    
  }
  #box-stats tr.column th, #box-stats tr.column th.section, #box-stats tr.column th.team_name {
    background-color: gray;
    color: #222;
    border-left: 1px solid #CCC;
  }

  #box-stats tr:hover td{
    background-color: #111;
    color: #ccc;
  }
   #box-stats .total{
    background-color: #111;
  }
  /* End stats */

