css:
@-moz-document domain("nyaa.se") {
  
:root {
--bg: transparent !important;
--bg-alt: transparent !important;
--header-bg: transparent !important;  
--main-text: #0ce !important;
--secondary-text: #74857C !important;
--third-text: #556B82 !important;  
} 
  
  body {
    background: url("http://a1decals.com/wp-content/uploads/2015/11/Black-Woodgrain-Background-Wallpaper4.jpg") !important;
    background-attachment: fixed !important;
    background-size: cover !important;
  }  
  
body, .viewdescription, .viewfile, .comment, .inputsearchcategory {
background-color: var(--bg) !important;
color: var(--main-text) !important;  
}  
    
.content, .page:hover, .tlisticon, .aplus, input, select, #tabnav {
background: var(--bg-alt) !important;  
}  
  
#navbar, #searchcontainer, #topbar {
    background: var(--header-bg) !important;  
  }  
  
#tabnav a, a, .tlistln, .tlistmn {
color: var(--main-text) !important;
}  
    
#tabnav a, a, .tlistln, .tlistmn {
color: var(--secondary-text) !important;
}
  
a:hover, .tlistsn, .tlistdn, #tabnav a:hover, span {
color: var(--third-text) !important;  
text-decoration: none !important;  
}  
  
.content, .page, .pagelink, tr.tlistrow, .tlistsize, .viewdescription, .viewfile, .comment {
  border: none !important;
}  
  
.content {
  margin-top: 80px !important;
  margin-left: 60px !important;
  }  
  
#topbar {
  height: 60px !important;
  margin-top: 30px !important;  
  width: 70% !important;
  left: 13% !important;
  position: absolute !important;
  } 
  
#tabnav {
  margin-top: 10px !important;  
  }    
    
#tabnav {
    padding: 10px !important;
    margin-top: 0px !important;
  }  
  
input, select {

  border: none !important;  
  padding: 0px !important;
  }  
  
 
  
#searchcontainer {
    padding: 0px !important;
  }  
  
.inputsearchterm {
   float: right !important;
  }
  
#tabnav {
    float: left !important;
  }  
  
.torrentcats, .inputsearchterm {
    text-align: center !important;
  }  
  
.torrentcats {
    width: 100% !important;
  }  
  
tr.trusted.tlistrow {
    background-color:  var(--header-bg) !important;  
}
.remake {
    background: var(--bg-alt) !important;  
}    
  
  #main > div:nth-child(4) > a:nth-child(1) > img:nth-child(1) {
    margin-left: 400px !important;
  }  
  
   
    
body, a, td {
    font-size: 14px !important;
  }  
  
.content, .viewdescription, .viewfile, .comment {
border-radius: 0px !important;  
}  
  
.content, #topbar {
box-shadow: 3px 3px 4px 3px rgba(0,0,0,0.1)  !important;
}    
  
#main > div:nth-child(2) > a:nth-child(1) > img:nth-child(1), #main > div:nth-child(1) > div:nth-child(1) > a:nth-child(1) > img:nth-child(1), .inputsearchsubmit, div.pages:nth-child(4), div.leftpages:nth-child(3) > span:nth-child(2), div.leftpages:nth-child(3) > span:nth-child(1), div.rightpages:nth-child(5) > a:nth-child(1), div.rightpages:nth-child(5) > a:nth-child(2), .refinesearch, .notice, #main > div:nth-child(2) > div:nth-child(1) > a:nth-child(1) > img:nth-child(1), #main > div:nth-child(1) > div:nth-child(1) > div:nth-child(1), #main > div:nth-child(2) > div:nth-child(1) > div:nth-child(1), #main > div:nth-child(4) > div:nth-child(1) {
    display: none !important;
  }  
  
img {
opacity: 0.7 !important;  
}  
img:hover {
opacity: 0.3 !important;  
  transition: ease-in-out .3s !important;
}    
  
  
  
  
  
  
  
  
}
