css:
@-moz-document regexp(".+\\/www.atheistrepublic.com/blogs"), regexp(".+\\/www.atheistrepublic.com/blogs?.+") {
#content-wrapper .container {
    background-color: #222;
}
.views-row,
.bblock,
.view-content {
    background-color: #444;
    color: #f5f5f5
}
.bblock {
    margin-top: -25px;
}
.views-field.views-field-field-featured-image {
    margin: 0px 10px 5px 10px;
}
.blog-teaser p,
.news-teaser p {
    margin-right: 10px;
}
.page-header,
.bblock,
.bheader,
.vblock .view-content {
    border-radius: 0
}

.field-content a {
 	color: #f5f5f5   
}

select {
  background:url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='50px' height='50px'><polyline points='46.139,15.518 25.166,36.49 4.193,15.519'/></svg>");
  background-color:white;
  background-repeat:no-repeat;
  background-position: right 8px top 15px;
  background-size: 16px 16px;
  color:white;
  padding:12px;
  width:auto;
  font-family:arial,tahoma;
  font-size:13px;
  font-weight:bold;
  text-align:center;
  text-shadow:0 -1px 0 rgba(0, 0, 0, 0.25);
  border-radius:0;
  -webkit-border-radius:0;
  -webkit-appearance: none;
  border:0;
  outline:0;
  -webkit-transition:0.3s ease all;
	   -moz-transition:0.3s ease all;
	    -ms-transition:0.3s ease all;
	     -o-transition:0.3s ease all;
	        transition:0.3s ease all;
}

input {
  color:white;
  padding:12px;
  width:auto;
  font-family:arial,tahoma;
  font-size:13px;
  font-weight:bold;
  text-align:center;
  text-shadow:0 -1px 0 rgba(0, 0, 0, 0.25);
  border-radius:0;
  -webkit-border-radius:0;
  -webkit-appearance: none;
  border:0;
  outline:0;
  -webkit-transition:0.3s ease all;
	   -moz-transition:0.3s ease all;
	    -ms-transition:0.3s ease all;
	     -o-transition:0.3s ease all;
	        transition:0.3s ease all;
}

select, input {
  background-color:#444;
}

.form-item-search-api-views-fulltext input, #edit-search-api-views-fulltext {
 	color: white;   
    width: 100%;
}

select:hover, input:hover {
  background-color:#555;
}

select:focus, select:active, input {
  border:0;
  outline:0;
}

.views-exposed-widget .views-submit-button {
 	padding-top: 0;   
}

.views-exposed-form .views-exposed-widget .form-submit {
    margin-top: 1.1em;
}

#header-wrapper {
    display: none;
}

div.bcontent {
    display: none;
}

div.sharethis-wrapper {
    display: none;
}

div.panel-pane.pane-block.pane-block-48.pane-block.no-title.block {
    display: none;
}

div.panel-pane.pane-block.pane-block-49.pane-block.no-title.block {
    display: none;
}

div.panel-pane.pane-block.pane-block-187.pane-block.donateblock.no-title.block {
    display: none;
}

div.panel-pane.pane-block.pane-widgets-s-facebook-like-box.pane-widgets.no-title.block {
    display: none;
}

#follow-top {
    display: none;
}

h2.padded {
    display: none;
}

div.region.region-footer {
    border-top-width: 0;
}

#page-title {
    border-radius: 0px;
}

#content-wrapper {
    background-color: #222;
}

div.views-exposed-widget.views-widget-filter-field_blogger_tid {
    color: #e8e8e8;
}

div.views-exposed-widget.views-widget-filter-field_blog_tags_tid label {
    color: #ebebeb;
}

div.blog-teaser p {
    color: #ebebeb;
}

div.views-field.views-field-nothing-1 {
    border-radius: 0px;
}

div.readmore {
    margin-right: 20px;
    color: #dedede;
}

ul.pager {
 	background-color: #444;
    border: 0;
    border-radius:0;
    color: white;
}

a {
 	color: white;
    text-decoration: underline;
}

.blogs-filter {
    float: left;
    font-size: .8em;
}

#block-panels-mini-blogs-top {
 	display: none;   
}

.ctitle a {
 	text-decoration: none;   
}

body {
 	background-color: #222;   
}

.breadcrumb {
  	color: white;  
}

#footer-wrapper, #footer-wrapper .container {
 	background-color: #111
}

.pane-block-196 {
 	display: none;   
}

#logouttools .first {
 	background-color: #666;   
}

.user-profile-icons img, .login-icons img {
 	border: 0;   
    -webkit-filter: brightness(100); filter: brightness(100);
}
}
