css:
@-moz-document regexp("https?://(?!us-mg6.mail\\.)([0-9A-Za-z]+\\.)?([0-9A-Za-z]+.)?yahoo\\.com.*") {
body,
html,
#UH {
	background: #E0F2F1!important;
}
#Eyebrow,
#masterNav {
	background-color: #009688 !important;
}
#UHSearchWeb,
.Themable .ThemeReset #UHSearchWeb {
	background: #00695C !important;
	border: 0;
	box-shadow: 0 2px #00695C !important;
}
.yucs-trigger .Ycon {
	color: #00695C!important;
}
.has-scrolled #UH {
	-webkit-box-shadow: 0 0 9px 0 #500095;
	-moz-box-shadow: 0 0 9px 0 #500095;
	box-shadow: 0 0 9px 0 #00695C!important;
	border-bottom: 1px solid #00695C!important;
}
.StencilRoot .App,
.StencilRoot .App_v2,
.Themable .ThemeReset .App,
.Themable .ThemeReset .App_v2 {
	background-image: -webkit-linear-gradient(top, #009688 0, #009688 100%) !important;
	outline-color: #ececec;
	border-radius: 3px;
	border-color: transparent! important;
}
.hpset-gradient {
	_background-color: #400090;
	background-image: -webkit-linear-gradient(top, rgb(105, 0, 136) 0%, rgb(64, 0, 144) 100%);
	background-image: -webkit-linear-gradient(top, #009688 0, #009688 100%) !important;
	background-image: -ms-linear-gradient(top, rgb(105, 0, 136) 0%, rgb(64, 0, 144) 100%)!important;
	background-image: -o-linear-gradient(top, rgb(105, 0, 136) 0%, rgb(64, 0, 144) 100%)!important;
	background-image: -moz-linear-gradient(center top, #009688 0px, #009688 100%)!important;
	background-image: linear-gradient(top, rgb(105, 0, 136) 0%, rgb(64, 0, 144) 100%);
	border-radius: 3px;
	border-color: transparent;
}
.StencilRoot .HeaderBar,
.Themable .ThemeReset .HeaderBar {
	background-color: #00695C !important;
}
.StencilRoot:link,
.StencilRoot:visited {
	color: #55d0c9 !important;
}
StencilRoot .Themable .ThemeReset:link,
.StencilRoot .Themable .ThemeReset:visited,
.StencilRoot:link,
.StencilRoot:visited {
	color: #55d0c9 !important;
}
#Eyebrow #yucs-home-menu a,
#Eyebrow #yucs-top-menu a,
.HomeDropDown-on #yucs-home-link b,
.HomeDropDown-on #yucs-home-link i,
.MoreDropDown-on #yucs-more-link b,
.MoreDropDown-on #yucs-more-link i,
.yucs-trigger b {
	color: #00695C !important;
}
#set-homepage {
	color: #fff !important;
}
.BottomBorderTag .Selected:after {
	background: #0ab0a0 !important;
	content: " ";
	position: absolute;
	left: 0;
	right: 0;
	height: 5px;
	top: 100%;
	margin-top: -5px;
}
.today .FilmstripBtn {
	background-color: #fff;
	color: #0c9ba2 !important;
}
.StencilRoot body {
	color: #ffffff !important;
}
.StencilRoot .Primary,
.Themable .ThemeReset .Primary {
	color: #fff!important;
	background: #0ee9ce !important;
	border: 1px solid rgba(17, 200, 171, 0.71)!important;
}
.StencilRoot .Themable .ThemeReset :link, .StencilRoot .Themable .ThemeReset :visited, .StencilRoot :link, .StencilRoot :visited {
    color: #32e1d9 !important;
}
}
