css:
@-moz-document domain("mydramalist.com") {
body{
    background				: #444444!important;
    background-attachment	: fixed !important;
    background-size			: cover !important;
    background-position		: center center !important;
}

#header {
    background				: #333333;
}
    
#content-top > div.content, .spnsr_right,
#footer{
	display					: none !important;
}
body {
	text-shadow		: 0 0 0 #000;
    -webkit-filter			: brightness(60%) contrast(110%) invert(.05);
    -ms-filter				: brightness(60%) contrast(110%) invert(.05);
    filter					: brightness(60%) contrast(110%) invert(.05);
  }

}
