@-moz-document domain("roblox.com") {
.ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard{display:none !important}.ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard{display:none !important}

#header {
    	opacity: 1;    
    	background-color: #9C1E1E;
    	/*display: none;*/
	}
    
.rbx-scrollbar li .rbx-highlight {
        background: #9C1E1E;
    }
.rbx-header .rbx-nav-collapse .rbx-nav-notification {
    	background: #9C1E1E;
	}
.forum-table-header, body .forum-table-header th, body .forum-table-footer, #ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th, #ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th {
        background: #9C1E1E !important;
    }

.rbx-upgrade-now #upgrade-now-button, .rbx-btn-secondary-xs.btn-more, #AjaxCommentsContainer > div > div.comment-form > form > button {
  		background-color: #9C1E1E !important;
   		border-color: #9C1E1E;
    }

.rbx-upgrade-now #upgrade-now-button:hover, .rbx-btn-secondary-xs.btn-more:hover, #AjaxCommentsContainer > div > div.comment-form > form > button:hover {
        background: #9C1E1E !important;
        border-color: #9C1E1E;
    }

.navigation .notification-icon {
        background: #9C1E1E;
    }

#MasterContainer, .nav-content-inner, .container-main, .gutter-ads {
        background: url(https://wallpaperscraft.com/image/red_solid_light_bright_scarlet_65848_3840x2400.jpg) top center repeat-x #9C1E1E
    }
    
#navbar-search-input {
		border: 1px solid #9C1E1E;
    }
#party_none_title, div.friend_dock_chatbox_titlebar.chat-header-blink-off {
		background: linear-gradient(to bottom,#9C1E1E,#9C1E1E);
    }
#ctl00_cphRoblox_RightColumn, #Body > table > tbody > tr > td:nth-child(3), #Body > table > tbody > tr > td:nth-child(4) {
  		display: none;
    }
