css:
@-moz-document domain("input.club") {
html,
body {
    background-color: black!important;
    color: #aaa;
}

#fusion-gmap-container, #main, #sliders-container, #wrapper, .bbp-arrow, .fusion-separator .icon-wrapper, body, html {
    background-color: #000;
}

h1, h2, h3, h4, h5, h6 {
    	color: #bbb!important;
}

#main .post h2 a, .about-author .title a, .bbp-forum-header a.bbp-forum-permalink, .bbp-reply-header a.bbp-reply-permalink, .bbp-topic-header a.bbp-topic-permalink, .fusion-content-widget-area .widget .recentcomments, .fusion-content-widget-area .widget li a, .fusion-content-widget-area .widget_categories li, .fusion-load-more-button, .fusion-rollover a, .project-content .project-info .project-info-box a, .shop_attributes tr th, .single-navigation a[rel="next"]:after, .single-navigation a[rel="prev"]:before, body a, body a:after, body a:before {
    color: #6ebaea;
}

.fusion-fullwidth-1{
	border-color: rgb(10, 85, 101)!important;
}

.fusion-header, .layout-boxed-mode .side-header-wrapper, .side-header-background {
    background-color: #333!important;
    border-bottom: 1px solid #555!important;
}

.fusion-main-menu .fusion-widget-cart-counter > a:before, .fusion-main-menu > ul > li > a {
    color: #ccc;
}

img{
opacity: 0.4;
}
img:hover {
    opacity:.7}


.fusion-imageframe {
border: 1px solid black!important;
}
}
