css:
@-moz-document domain("twitch.tv"), domain("multistre.am"), domain("multitwitch.tv") {

/*[[theme-d-g]]*/

*, *:active {
    box-shadow: none !important;
    text-decoration: none !important;
}
.tw-theme--dark .carousel-nav__img-container, .tw-theme--light .carousel-nav__img-container {
    background: transparent !important;
}
.tw-input:focus, .tw-theme--dark .tw-input:focus, .dash-column select.form__input:focus, .dash-column textarea.form__input:focus, .dash-column .form__input[type="text"]:focus, .top-nav__search input[type="text"]:focus, div.settings-root__content.tw-pd-y-2 div.tw-border-b.tw-border-bottom-left-radius-medium.tw-border-bottom-right-radius-medium.tw-border-l.tw-border-r.tw-border-t.tw-border-top-left-radius-medium.tw-border-top-right-radius-medium.tw-c-background.tw-mg-b-4, .settings-row, .side-nav__theme-wrapper.tw-border-r, .tw-border-b, .tw-border-l, .tw-border-r, .tw-border-t, .edit-broadcast__container .tw-border-t, .top-nav-user-menu-dropdown.tw-border-b {
