}
.horizontal-carousel-player__details--overlay, .tw-theme--dark .tw-c-background-alt, .channel-header, .channel-info-bar, .chat-viewers__container .tw-full-height, .tw-theme--dark .side-nav-card__link, .brick, .vod-vertical-nav, .fuel-bar, .theme--dark .social-column, .notification-settings__body > div:first-child, .clips-edit-top-nav, .clips-post-edit-clip-wrapper .clips-post-edit-sidebar, .clips-post-edit-share, .whispers-thread, .top-nav-user-menu-dropdown div.tw-c-background-alt, .drag-and-drop-card-container, .dashboard-centered-page .tw-c-background-base {
    background: var(--bg2) !important;
    background-color: var(--bg2) !important;
}
.simplebar-scroll-content .tw-c-background-alt-2, .search-result-section__titlesep, .room-selector__header, .chat-line__message.bttv-split-chat-alt-bg, .vod-message.bttv-split-chat-alt-bg, .whispers-threads-box__header, div.tw-border-t:nth-child(3), .inbox-info div, [data-test-selector="center-window__balloon"] .tw-interactable, .clips-top-nav, .clips-top-nav-user, .clips-top-nav-user__dropdown, .clips-sidebar-info > div > div > div, .thread-header__title-bar-container, .whispers-thread .tw-button-icon, .side-nav__theme-wrapper div, .top-nav-user-menu-dropdown.tw-c-background, .tw-balloon > .tw-c-background-base {
