    background-color: var(--bg3) !important;
}
.top-nav__menu, .tw-theme--dark .tw-c-background, .whispers-list-item:hover, .top-nav__logo, .onsite-notification-toast, .vod-message.vod-message--focused, .vod-message:hover, [data-test-selector="center-window__balloon"] .tw-c-background-alt, .clips-watch > div > div:nth-child(2), .top-nav-user-menu-dropdown.tw-c-background-base, .dashboard-side-nav {
    background: var(--bg4) !important;
    background-color: var(--bg4) !important;
}
.tw-interactable--hover, .tw-interactable:hover, .tw-theme--dark .side-nav-card__link:hover, .video-carousel:hover .video-carousel__button, .tw-balloon .tw-c-background-alt-2, .channel-header__item::before {
    background: var(--bg5) !important;
    background-color: var(--bg5) !important;
}
.tw-theme--dark .tw-interactable--hover, .tw-theme--dark .tw-interactable:hover, .dashboard-side-nav__link--active, .chat-settings__content, .chat-settings__content div:nth-child(3), .chat-settings__content div:nth-child(1) {
    background: var(--bg6) !important;
    background-color: var(--bg6) !important;
}




.side-nav__toggle-visibility, .right-column__toggle-visibility, .mention-fragment {
