}
.chat-room__content, .dash-widget, .dash-column, div.tw-flex-row:nth-child(2), .video-chat, .chennel-page-layout > div > div:nth-child(2), .settings-section div, .settings-section, .channel-page-layout > div > div:nth-child(2) {
    border-color: var(--bg1) !important;
}
.channel-header, .channel-info-bar, .brick, .vod-vertical-nav, .fuel-bar, .clips-edit-top-nav {
    border-color: var(--bg2) !important;
}
.room-selector__header, .pinned-cheer-v2 > div:nth-child(1), .chat-viewers__pane, .whispers-threads-box__header, div.tw-border-t:nth-child(3), .carousel-nav__item, .clips-top-nav {
