}
.carousel-nav__item--active, .tw-theme--dark .carousel-nav__item--active, .tw-theme--light .carousel-nav__item--active, .directory-tabs__item:hover::after, .tw-theme--dark .tw-border-b, .fuel-bar__separator, .persistent-notification, .persistent-notification__read, [data-test-selector="center-window__balloon"] .tw-c-background-alt, .persistent-notification__unread, .prime-offer__new-offer-header, .prime-offer--vertical, .prime-offers-upsell {
