    color: #fff !important;
}






.anon-front__social-container, .blue-bar, .tw-balloon--down > .tw-balloon__tail, .room-picker {
    display: none !important;
}
.scrollable-area .simplebar-track .simplebar-scrollbar {
    border-radius: 0;
    box-shadow: none;
    background: var(--bg9);
}
.top-nav .tw-button, .tw-theme--dark .tw-button, .tw-theme--light .tw-button {
    background: var(--bg6) !important;
}
.tw-button:hover, .tw-theme--dark .tw-button:hover, .tw-theme--light .tw-button:hover {
    background: var(--bg9) !important;
    border-color: var(--bg9) !important;
    color: #fff !important;
}
.tw-theme--dark .side-nav__overlay-wrapper, .tw-theme--dark .side-nav__theme-wrapper {
    border-color: var(--bg2) !important;
    background: var(--bg2);
}
.directory-tabs {
    background: var(--bg2) !important;
    border: none;
}
.directory-tabs__item::before {
    background: var(--bg4);
}
.tw-button--hollow, .tw-button-icon--hollow {
    background: var(--bg4) !important;
    border-color: var(--bg4) !important;
    color: #fff !important;
}
.tw-balloon > div {
    border-color: var(--bg3) !important;
    background: var(--bg3) !important;
    color: #fff !important;
}
.tw-theme--dark .tw-checkbox__label::before {
    box-shadow: inset 0 0 0 1px var(--bg9), 0 0 0 transparent !important;
}
.whispers .tw-c-background-alt-2, .whispers-threads-box__coxXL3617xG1BB0Xxntainer {
    border-color: var(--bg7) !important;
    background: var(--bg7) !important;
}
.tw-theme--dark .tw-border-t {
    border-color: var(--bg6) !important
}
.tw-tab-nav {
    box-shadow: inset 0 -1px 0 0 var(--bg6) !important
}
.tw-tab-nav__item:not(.tw-tab-nav__item--disabled) a.active, .tw-tab-nav__item:not(.tw-tab-nav__item--disabled) button.active {
    box-shadow: inset 0 -1px 0 0 var(--purple) !important;
}
.channel-header__follow-button button {
    background: var(--bg5) !important;
}
.tw-textarea {
    background: var(--bg2) !important;
    border-color: var(--bg2) !important;
    color: #fff !important;
}
svg.tw-svg__asset.tw-svg__asset--glyphlive.tw-svg__asset--inherit {
    color: red !important;
}
.chat-viewers__header {
    background: var(--bg5) !important;
    border-color: var(--bg5) !important;
    color: #fff;
}
.channel-header__follow-button button.tw-button--success {
    background: #14b866 !important;
}
.channel-header__follow-button button.tw-button--success:hover {
    background: #bd0f0f !important;
    border-color: #bd0f0f !important;
}
.chat-viewers-list .tw-upcase {
    font-weight: 900;
}
.whispers-threads-box__container {
    border-color: var(--bg4) !important;
}

div.sticky-container div.tw-elevation-1.tw-c-background.tw-hide.tw-md-block div.tw-c-background-alt.tw-pd-1 {
    background: var(--bg2) !important;
}
.clips-preview-card__title {
    font-weight: 600;
    color: #fff;
}
.side-nav__theme-wrapper {
    border-right: 1px solid var(--bg4) !important;
}
.prime-offer-list--vertical .prime-offer-list__content::after {
    background-image: linear-gradient(transparent,rgba(12,12,12,0.6))
}
.drag-and-drop-card-content .tw-textarea {
    background: var(--bg4) !important;
}





