}
.top-nav__nav-link, .tw-input svg, .directory-tabs__item, .stream-thumbnail__card h3, .tw-checkbox__label, .tw-svg__asset--inherit, .tw-theme--dark .tw-svg__asset--inherit, .tw-theme--light .tw-svg__asset--inherit, .tw-tab-nav__item:not(.tw-tab-nav__item--disabled) button.active, .live-channel-card__channel h3, .video-preview-card__video-title, .tw-c-text-alt-2, .channel-header__item, .default-panel .panel-description li, .default-panel .panel-description p, .chat-rules li, .tw-ellipsis, .user-notice-line .tw-strong, .chat-viewers-list__button, .tw-tab-nav__item:not(.tw-tab-nav__item--disabled) a, .tw-tab-nav__item:not(.tw-tab-nav__item--disabled) button, .whispers-threads-box__header, .c-text-alt-2, .typeset p, .typeset li, .typeset dd, .feed-item-text, div.tw-flex-row:nth-child(2), .inbox-info div, .tw-radio__label, h6, p, body, html, .settings-row {
