}
.tw-box-art-card:hover .tw-box-art-card__title, .anon-top-channels:hover h3, .directory-tabs__item--selected, .directory-tabs__item:hover, .stream-thumbnail__card:hover h3, .live-channel-card:hover h3, .tw-tab-nav__item:not(.tw-tab-nav__item--disabled) a.active {
