css:
@-moz-document domain("theartistunion.com") {
.content-page .item-list {
    -webkit-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -moz-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -o-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    box-shadow: 0 0 4px 0 rgb(119, 47, 197);
    background-color: #232323;
    border-radius: 4px;
    margin: 0 0 60px 0;
    padding: 20px;
}
.track-body .track-body--title a {
    color: #647075;
}
.welcome-card {
    -webkit-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -moz-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -o-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    background-color: #353535;
    border-radius: 4px;
    margin: 0 auto 20px auto;
    padding: 20px;
    text-align: center;
}
body {
    background: #1b1b1b;
    color: rgba(111, 124, 130, 0.6);
    font-family: 'Gotham', 'Avenir', 'Helvetica', sans-serif;
    font-size: 12px;
    font-style: normal;
    font-weight: 400;
    min-height: calc(100% - 60px);
    position: relative;
}
.side-banner {
    -webkit-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -moz-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -o-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    background-color: rgba(35, 35, 35, 0.6);
    border-radius: 4px;
    margin: 0 auto 20px auto;
    padding: 20px 10px;
    text-align: center;
}
.navbar-default {
    position: fixed;
    top: 0;
    right: 0;
    left: 0;
    -webkit-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -moz-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -o-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    background-color: rgb(119, 47, 197);
    border: none;
    border-radius: 0;
    border-top: 1px solid rgba(41, 39, 44, 0.25);
    margin: 0;
    z-index: 1000;
}
.welcome-card .welcome-card--callout {
    color: #777777;
    font-size: 12px;
    font-weight: 400;
    line-height: 24px;
    margin: 20px auto 0 auto;
}
.artist-card {
    -webkit-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -moz-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -o-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    background-color: rgba(143, 44, 250, 0.8);
    border-radius: 4px;
    color: #f2f2f2;
    display: block;
    font-size: 14px;
    font-weight: 600;
    margin: 0 auto 20px auto;
    padding: 10px;
    text-align: center;
    margin-left: 878px;
    margin-top: 25px;
}
.content-page {
    position: relative;
    margin-top: -200px;
}
.static .static--content {
    -webkit-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -moz-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -o-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    background-color: #2b2b2b;
    border-radius: 4px;
    margin: 0;
    padding: 20px;
}
.form-card {
    -webkit-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -moz-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    -o-box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    box-shadow: 0 0 4px 0 rgba(41, 39, 44, 0.25);
    background-color: #ccc;
    border-radius: 4px;
    color: #29272c;
    margin: 20px auto;
    max-width: 360px;
    padding: 20px;
}
}
