css:
@-moz-document domain("mega.nz") {
* :not(span):not(i) {
    background-color: #151412 !important;
}
.info-txt span,
.download .dark-numbers,.active-folder-link .tranfer-filetype-txt {
    color: #FFF !important;
}
.bottom-page.full-block,
.top-head,
.pages-nav,
.warning-block,
.fm-dialog-overlay,
.limited-bandwidth-dialog {
    display: none !important;
}
.download.big-button:not(.red)::after,
.share.big-button:not(.red)::after {
    background: #222 !important;
}
.checkboxOn::before,
.checkboxOff::before,
.checkboxOn::after,
.checkboxOff::after {
    -webkit-box-shadow: 0 0 0 1px #eeeeee !important;
    box-shadow: 0 0 0 1px #eeeeee !important;
}
.grid-table .megaListItem.ui-selected td,.grid-table .megaListItem:hover td {
    background-color: rgb(32, 32, 32) !important;
    color:#FFF;
}
.transfer-widget *:not(i),.transfer-widget.info-block .dark {
    background-color: rgb(32, 32, 32) !important;
    color:#FFF;
}
.dropdown-white-arrow{
    filter: invert(87%);
}
.files-grid-view.fm .grid-scrolling-table, .files-grid-view.fm .grid-scrolling-table .jspPane, .transfer-scrolling-table, .transfer-scrolling-table .jspPane {
    background-image: none;
}
/*Change 'main-blur-block' to 'main-block' */
}
