css:
@-moz-document domain("serv.ads.pixiv.org") {
a[href^="https://serv.ads.pixiv.org/"] {
    display: none !important;
}
}

@-moz-document url-prefix("https://www.pixiv.net/mypage.php"), url-prefix("https://www.pixiv.net/cate_r18.php") {
.my-profile-unit,
/*プロフィール*/

.menu-unit
/*履歴*/

{
    display: none !important;
}
}

@-moz-document domain("www.pixiv.net") {
/*広告非表示*/

#header-banner,
.ad,
.ad-footer,
.ads_sensei,
.ad-printservice,
#aswift_0_anchor,
#touch_introduction,
.ads_value,
.recruit-ad,
.ads_area,
a[href^="http://serv.ads.pixiv.org/"],
iframe[src^="http://serv.ads.pixiv.org/"],
div[id^="adingoBeagle"],
div[id^="div-gpt-ad-"],
.comic-container,
.ad-bigbanner,
.worksImageresponse,
/*pixivコミック*/

.iracon-ad
/*いらこん*/

{
    display: none !important;
}
/*--サイト外観--------*/

.layout-wrapper h1.title,
/*ロゴ*/

li.tag a.portal,
/*タグについてるポータルへのリンク*/

NAV.category-list
/*検索バーの下の小説・イベントの奴非表示*/

,.upload
{
    display: none !important;
}
/*ヘッダの高さ*/

.layout-wrapper {
    height: 45px !important;
}
/*検索バーの位置*/

.ui-search {
    bottom: 10px !important;
    right: 250px !important;
}
/*ポップボードの位置*/

.notifications {
    left: 735px !important;
}
/*背景色固定*/
/*
body {
    background: #EDF0F5 !important;
}*/
/*タグの背景画像削除*/

li.tag {
    background-image: none !important;
    box-shadow: none !important;
    border: none !important;
}
/*---プレミアム系---------*/

#toolbar-items,
._require-premium,
.require-premium,
.sprites-premium,
.profile-about-premium,
a[href*="/premium.php"],
img[src*="premium"],
#popular-trial
/*人気検索宣伝*/

{
    display: none !important;
}
/*---トップページ---------*/

#column-misc section:nth-child(4),
/*コミケ情報*/

#column-misc section:nth-child(6),
/*pixivニュース*/

.group-list
/*グループ*/

{
    display: none !important;
}
/*--検索ページ--------*/

nav.breadcrumb,
/*パンくずリスト*/

p.user,
/*ユーザ名*/

.showcase,
/*ショーケース*/

.popular-introduction
/*人気作品*/
{
    display: none !important;
}
/*サムネイルの枠非表示*/

li.image p img {
    border: none !important;
}
/*タイトルの太文字を解除・タイトルの上下を広げる*/

li.image h2 {
    font-weight: normal !important;
    line-height: 140% !important;
    margin-top: 0.5em !important;
    margin-bottom: 1em !important;
}
/*人気作品を挟んだ時の隙間をなくす*/

.images li {
    height: 250px !important;
}
/*件数を見やすくする*/

.count,
.count-badge {
    font-size: 100% !important;
    color: #000000 !important;
    background-color: transparent !important;
}
/*--個別ページ--------*/

.share-button
/*SNSボタン*/

{
    display: none !important;
}
/*スタンプ縮小*/

img[src^="http://source.pixiv.net/common//images/stamp/stamps/"] {
    width: 30px !important;
    height: 30px !important;
}
/*--そのた--------*/

.new-badge
/*New!のやつ*/

{
    display: none !important;
}
.layout-column-1,
.ui-layout-west {
    float: right !important;
}
.layout-column-2,
.ui-layout-east {
    float: left !important;
}
/* 色指定 */
/*背景色固定*/
body,._icon-history,.wrapper,div.content{
    background: #000 !important;
}
._unit,.work-tags,.area_new{
    background: hsl(1,0%,10%) !important;
}
.column-menu,.rounded,#item-container,.end-page,.worksShare,.worksListOthersTitle,.container,._no-item,#favorite-preference,header>h1,.mf__following-alert,.official-contest,._fanbox-entry,.NewsTop h1,div#column-misc section.item h1, div#column-misc section.item h2.item-head,div#column-misc section.item
,div#column-misc section.item ol.ranking > li:nth-child(odd),.my-profile-unit .user-link,.booth-follow-items .information
,.td1,.td2,
/*nobel*/.novel-body,.stacc_center_area{
    background: hsla(1,0%,18%,0.9) !important;
}
/* フォント色調 */
.title,dt,dd,h1,p,#ui-scroll,.self,.td2,.current,label,.td1,th{
    color:#fafafa !important;
}
a{
    color:hsla(182, 67%, 45%, 1) !important;
}
ul.tabs>li>a.current{
color:#000 !important;
}
._global-header{
    background: hsla(190,100%,26%,0.4) !important;
border-bottom:1px solid hsla(190,100%,26%,.3) !important;
}
footer{
display:none !important;
}
}

@-moz-document url-prefix("https://www.pixiv.net/search.php"), url-prefix("https://www.pixiv.net/recommended.php"), url-prefix("https://www.pixiv.net/bookmark"), url-prefix("https://www.pixiv.net/ranking.php") {
@media screen and (min-width:1199px) and ( max-width:1398px) {
    .layout-body {
        width: 1170px;
    }
    #wrapper {
        width: 1200px;
    }
}
@media screen and (min-width:1399px) {
    .layout-body {
        width: 1372px;
    }
    #wrapper {
        width: 1400px;
    }
}
.comic-hot-works,.footer,.announce-ending-popup {
    display: none !important;
}
}

@-moz-document url-prefix("https://www.pixiv.net/member_illust.php"), url-prefix("https://www.pixiv.net/bookmark.php") {
/*メンバーごとのページ*/
#illust-recommend{
   margin-top:900px;
}
@media screen and (min-width:1549px) {
    #wrapper {
        width: 1550px;
    }
    .layout-a {
        width: 1176px !important;
    }
    .layout-column-2 {
        width: 980px !important;
    }
}
}

@-moz-document url-prefix("https://www.pixiv.net/member_illust.php") {
.worksImageresponse {
    display: none !important;
}
.user-reaction{
display:none;
}

.worksShare{
display:none !important;
}
}

@-moz-document url-prefix("https://www.pixiv.net/stacc/") {
.stacc_right_area,.stacc_speak_form {
    display: none !important;
}
.stacc_center_area{
        width: 742px;
}

ref_thumb_left {
    width: 450px;
}
}
