css:
@-moz-document domain("mail.tiscali.it") {

/* remove righ frame with ad */
#dotnAd_120x600_dx { 
    width: 10px;
    display: none !important; }

/* remove button to switch on/off right frame */
#closeADVSide { display: none;}

/* resize messages' area */
#mainscreen {
    right: 8px;
    top: 44px;
}

/* remove adv from bottom of page */
div.autopromo { display: none !important; }

/* remove indoona adv on top of frame */
.indoonaTop { display: none !important; }

/* remove left and righ side background, leaving more space for messages */
.bodyCustomAdv #mainscreenContainer {
    width: 100% !important;
    margin-left: 0px !important;
    left: 0% !important;
}

/* remove main adv background image */
#advCustomA {
    background-image: none !important;
    display: none !important;
}

/* remove top frame background */
.bodyCustomAdv #mainscreenContainer { top: 0px !important; }
dotnAdDivSkLbImg { height: 0px }
#dotnAdDivSkSx, #dotnAdDivSkDx, #dotnAdDivSkLb, .viewSupportBlock { display: none !important; }

/* remove adv from button bar */ 
span.bannerTab { display: none !important; }

/* remove adv from left frame */
ul#boxlistcontent.leftboxlistcontent iframe._tiscaliIFrame { display: none !important; }
}
