css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.ptt.cc"),domain("ptt.cc") {/* Desktop */

@media screen and (min-width: 800px) {
    /*     gloabal */
    
    html, body {
        padding: 0;
        margin: 0;
        height: 100%;
    }
    .bbs-screen,
    #topbar,
    #navigation,
    .action-bar,
    #article-polling,
    #action-bar-container {
        min-width: 600px;
        max-width: 1200px;
        margin: 0 auto;
        border-radius: 3px 3px 0 0;
    }
    body {
        background-color: #e2e2e2;
    }
    .bbs-content,
    .r-list-container,
    .bbs-screen,
    .r-ent .nrec>span,
    html,
    body {
        font-family: "Helvetica Neue", "Luxi Sans", "DejaVu Sans", Tahoma, "Hiragino Sans GB", "Microsoft Yahei", sans-serif;
    }
    /*     bbs/list */
    
    .b-ent {
        padding: 5px 0;
    }
    .b-ent .board > * {
        font-family: inherit;
    }
    /*     bbs/index */
    
    .action-bar {
        height: 34px;
    }
    #action-bar-container {
        position: static;
        background-color: #fff;
        padding: 10px 10px 0 10px;
        border-bottom: 1px solid #e2e2e2;
        box-sizing: border-box;
    }
    #action-bar-container .btn {
        position: relative;
        display: inline-block;
        text-decoration: none;
        box-sizing: border-box;
        text-align: center;
        color: #888;
        border-radius: 3px;
        font-size: 13px;
        line-height: 13px;
        padding: 5px 8px 5px 8px;
        height: auto;
        background-color: #fff;
        margin-right: 5px;
        border: 0;
    }
    #action-bar-container .btn:hover {
        background-color: #f5f5f5;
        color: #000;
        text-decoration: none;
    }
    #action-bar-container .btn.selected {
        background-color: #334;
        color: #fff;
    }
    #action-bar-container .btn.disabled {
        color: #ddd;
        cursor: not-allowed;
    }
    #topbar-container {
        text-align: center;
        background-color: #fff;
        height: 44px;
        font-size: 15px;
        font-weight: 500;
        border-bottom: 1px solid rgba(0, 0, 0, 0.22);
        box-shadow: 0 0px 20px rgba(0, 0, 0, 0.22);
        padding: 0;
        position: static;
        box-sizing: border-box;
    }
    #main-container {
        padding: 20px 0 0 0;
        min-height: calc(100% - 65px);
    }
    #main-content {
        padding: 10px;
        box-sizing: border-box;
    }
    .r-list-container,
    .bbs-screen {
        background-color: #fff;
        border-radius: 3px;
        box-shadow: 0px 2px 3px rgba(0, 0, 0, 0.1);
        border-bottom: 1px solid #e2e2e2;
        margin: 0 auto;
        font-size: 14px;
        line-height: 1.6;
        color: #000;
        padding: 0;
    }
    .b-list-container {
        padding: 10px;
    }
    .r-list-container {
        border-radius: 0 0 3px 3px;
        margin-bottom: 20px;
    }
    #logo:link,
    #logo:visited,
    #logo:hover,
    #topbar .board,
    #topbar a:link,
    #topbar a:visited,
    #topbar a:hover {
        color: #556;
        text-decoration: none;
        font-size: 15px;
    }
    #topbar a:hover {
        background-color: #fff;
        color: #99a;
        text-decoration: none;
    }
    .r-ent {
        padding: 10px;
        font-size: 12px;
        line-height: 120%;
        text-align: left;
        border-bottom: 1px solid #e2e2e2;
        margin: 0;
    }
    .r-ent > * {
        margin: 0;
    }
    .r-ent .nrec {
        width: 5ex;
    }
    .r-ent .nrec>span {
        font-size: 12px;
        line-height: 12px;
        font-weight: bold;
        color: white;
        background-color: #aab0c6;
        display: inline-block;
        padding: 2px 10px 2px 10px;
        -moz-border-radius: 12px;
        -webkit-border-radius: 12px;
        border-radius: 12px;
        text-decoration: none;
        margin-right: 5px;
        text-shadow: none;
    }
    .r-ent .meta {
        padding-left: 13px;
    }
    .r-ent .meta >* {
        font-size: 14px;
        color: #ccc;
        height: auto;
        font-family: inherit;
    }
    .r-ent .meta .author {
        color: #778087;
        font-weight: bold;
    }
    .r-list-sep {
        height: 1ex;
        background-color: #4f4fab;
    }
    .title,
    .title a,
    .hl.f3 {
        color: #778087;
        text-decoration: none;
        word-break: break-all;
        font-size: 16px;
        line-height: 130%;
        text-shadow: 0px 1px 0px #fff;
        word-wrap: break-word;
        hyphens: auto;
    }
    .title:hover {
        cursor: pointer;
    }
    .title a:hover {
        background-color: #fff;
        color: #4d5256;
        text-decoration: underline;
    }
    .r-ent .title,
    .r-ent .meta {
        margin-left: 50px;
    }
    .article-metaline,
    .article-meta-tag,
    .article-meta-value {
        background-color: inherit;
    }
    .article-meta-value {
        padding: 0px;
    }
    .bbs-screen>.article-metaline:nth-child(3) {
        font-size: 24px;
        font-weight: 500;
        line-height: 150%;
        margin: 0;
        padding: 0px;
    }
    .bbs-screen>.article-metaline:nth-child(3)>.article-meta-value {
        color: #000;
    }
    .bbs-screen>.article-metaline:nth-child(4) {
        border-bottom: 1px solid #e2e2e2;
        padding: 0 10px 10px;
        margin: 0 -10px;
    }
    .article-meta-tag {
        display: none;
    }
    .article-metaline-right {
        top: 10px;
        right: 10px;
        padding: 5px 10px 5px 10px;
        line-height: 100%;
        background-color: #f0f0f0;
        border-radius: 10px;
        margin: 0px;
        display: inline-block;
    }
    .push {
        padding: 10px;
        font-size: 14px;
        line-height: 120%;
        text-align: left;
        border-bottom: 1px solid #e2e2e2;
        margin: 0 -10px;
        position: relative;
    }
    .bbs-screen span.f2+div.push {
        margin-top: 40px;
        box-shadow: 0px -2px 3px rgba(0, 0, 0, 0.1);
    }
    .bbs-screen span.f2+div.push:before {
        content: '';
        display: block;
        position: absolute;
        top: -30px;
        left: 10px;
        right: 10px;
        padding: 5px;
        font-size: 14px;
        line-height: 120%;
        margin: 0 -10px;
        background: #eeeeee;
        background: -moz-linear-gradient(top, #eeeeee 0%, #cccccc 100%);
        background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #eeeeee), color-stop(100%, #cccccc));
        background: -webkit-linear-gradient(top, #eeeeee 0%, #cccccc 100%);
        background: -o-linear-gradient(top, #eeeeee 0%, #cccccc 100%);
        background: -ms-linear-gradient(top, #eeeeee 0%, #cccccc 100%);
        background: linear-gradient(to bottom, #eeeeee 0%, #cccccc 100%);
        border-radius: 0px 0px 3px 3px;
        text-align: left;
    }
    .bbs-screen span.f2+div.push:after {
        position: absolute;
        top: -20px;
        content: '';
        display: block;
        background-color: #e2e2e2;
        left: -10px;
        right: -10px;
        height: 20px;
    }
    .push-tag {
        color: #990;
    }
    .push .push-userid {
        font-size: 12px;
        font-weight: bold;
    }
    .push .push-ipdatetime {
        color: #ccc;
    }
    .push .push-content {
        line-height: 1.6;
        color: #000;
    }
    #navigation-container {
        display: none;
    }
    #article-polling {
        display: block;
        font-size: 11px;
        background-color: #999;
        color: #ddd;
        margin: 10px auto;
        border-radius: 2px;
        padding: 4px;
    }
    .bbs-footer-message {
        content: '';
        display: block;
        max-width: 100%;
        width: 100%;
        border-top: 1px solid rgba(0, 0, 0, 0.22);
        background-color: #fff;
        text-align: center;
        color: #999;
        padding: 10px 0;
        font-size: 12px;
        line-height: 150%;
        margin-top: 20px;
    }
    img.over18-notice {
        width: 100px;
        margin-left: 25px;
    }
}
}
