css:
@-moz-document domain("steamcommunity.com") {
/*
	Steam Community
	Dark Cloud Theme
	By KraXarN
	Version 1.0.1
*/

/* General */

body {
    font-family: "Open Sans";
    background-color: rgb(20, 20, 20);
}

img {
    border-radius: 5px;
}

.content {
    font-family: "Open Sans Condensed";
    font-weight: Bold;
}

/* Scrollbar */

::-webkit-scrollbar {
	width: 12px;
}
 
::-webkit-scrollbar-track {
	display: none;
}
 
::-webkit-scrollbar-thumb {
	background-color: rgba(200, 200, 200, 0.5);
	border-radius: 5px;
}

/* Persona name */

.actual_persona_name {
    font-family: "Open Sans";
    font-weight: Bold;
}

/* Comments */

.commentthread_comment_text {
    font-family: "Open Sans Light";
}

/* Info box */

.showcase_notes {
    font-family: "Open Sans Light";
}

/* Header */

.profile_header {
    background-color: rgb(20, 20, 20);
    padding: 12px 24px 12px 24px;
    border-radius: 0px 0px 20px 20px;
}

.profile_header_bg_texture, .profile_header_bg {
    background-image: none;
}

div#global_header, div#global_header .content {
    background: #141414;
}

/* Buttons */

.btn_medium, .btn_small, .btn_small_thin {
    font-family: "Open Sans Condensed";
    font-weight: bold;
    background-image: none;
    background-color: rgb(20, 20, 20);
    border-radius: 15px;
    border: 0;
}

.btn_medium:hover, .btn_small:hover, .btn_small_thin:hover {
    background-image: none;
    background-color: rgb(20, 20, 20);
}

.btn_medium span, .btn_small span, .btn_small_thin span {
    background-image: none;
    background-color: rgb(20, 20, 20);
    border-radius: 15px;
}

.btn_medium span:hover, .btn_small span:hover, .btn_small_thin span:hover {
    background-image: none;
    background-color: rgb(30, 30, 30);
    transition: 0.1s ease-in-out;
}

/* Header badge */

.profile_header_badge {
    background-color: rgba(0, 0, 0, 0);
    box-shadow: 0px 0px 0px;
    border: 0;
}

/* Avatars */

.playerAvatar, .playerAvatar img {
    border-radius: 50%;
}

.group_content, .group_summary {
    background-image: none;
}

.profile_group_avatar, .profile_group_avatar img {
    border-radius: 50%;
}

/* Showcases */

.profile_customization {
    background-image: none;
    background-color: rgb(20, 20, 20);
}

.profile_background_overlay_content {
    background-color: rgb(20, 20, 20);
    border-radius: 20px 20px 0px 0px;
}

.recent_game .game_info_stats, .favoritegame_showcase .game_info_stats {
    background-color: rgb(20, 20, 20);
    border: 0;
}

.game_info_stats_rule {
    opacity: 0;
}

.achievement_progress_bar_ctn {
    background-color: rgb(30, 30, 30);
    border: 0;
    box-shadow: 0px 0px 0px rgb(20, 20, 20);
}

.game_info_stats {
    font-family: "Open Sans Light";
}

.game_info_badge_border {
    border: 0;
    background-color: rgb(20, 20, 20);
    box-shadow: 0px 0px 0px rgb(20, 20, 20);
}

/* Side text */

.profile_item_links, .profile_count_link_total {
    font-family: "Open Sans Light";
}

/* Notifications */

#header_notification_link {
    background-color: rgb(20, 20, 20);
    color: rgb(200, 200, 200);
}

.header_notification_dropdown_seperator {
    opacity: 0;
}

#account_pulldown {
    background-image: none;
}

/* VAC Ban */

.profile_ban_status, .profile_ban_status .whiteLink, .profile_ban_info {
    font-family: "Open Sans Light";
    color: rgb(100, 100, 100);
}

/* Popups */

.popup_block_new .popup_body {
    font-family: "Open Sans Light";
    background-image: none;
    background-color: rgb(20, 20, 20);
    border: 0;
}

.popup_block .popup_body {
    background-image: none;
    background-color: rgb(20, 20, 20);
    border: 0;
}

.inventory_iteminfo .item_desc_content {
    background-image: none;
    background-color:  rgb(20, 20, 20);
}

/* Popup windows */

.newmodal_header {
    background: #141414;
    font-family: "Open Sans Condensed";
    font-weight: bold;
}

.newmodal_content {
    background: #141414;
}

.abuseOptions {
    font-family: "Open Sans Light";
}

.abuseDescriptionCtn, #abuseDescriptionArea {
    background: rgb(30, 30, 30);
}

/* Image previes */

#modalContentTitleBar {
    background-color: rgb(20, 20, 20);
}

#modalContent.modal_frame {
    border: 2px solid #141414;
}

.mediaTop .mediaSidebar {
    background-color: rgb(20, 20, 20);
}

.avatarIcon img {
    border-radius: 50%;
    border: 2px solid rgb(97, 189, 255);
}

.iconHolder_online {
    background-image: none;
}

/* Profile hover */

.miniprofile_top, .miniprofile_ingame {
    background: #141414;
    border: 0;
}

.miniprofile_favorite_badge {
    background-color: #141414;
    border: 0;
}

/* Market */

.market_header_bg {
    background-color: rgb(20, 20, 20);
}

div.market_header_logo {
    background-image: none;
    background-color: rgb(20, 20, 20);
}

#BG_top > .user_info {
    background-color: rgb(20, 20, 20);
    border: 0;
}

#saleslimit, #market_headertip_saleslimit {
    background-color: rgb(20, 20, 20);
    border: 0;
}

/* Invites */

.profile_small_header_texture {
    background-image: none;
    background-color: rgb(20, 20, 20);
}

.profile_subpage_column .maincontent, body.migrated_profile_page .profile_subpage_column #BG_bottom.maincontent {
    background-image: none;
}

.leftBlockExtendedDetails {
    background-color: rgb(30, 30, 30);
}

/* Activity page */

.blotter_block {
    background: #141414;
}

.dynamiclink_box {
    border: 0;
    background-color: rgb(30, 30, 30);
    box-shadow: 0 0 2px rgb(0, 0, 0);
    border-radius: 5px;
}

.blotter_comment_thread .commentthread_entry_quotebox {
    border: 0;
}

.avatarMedium img {
    border-radius: 50%;
    border: 2px solid rgb(30, 30, 30);
}

/* Community home */

.apphub_background {
    background-image: none;
    background-color: rgb(20, 20, 20);
}

/* Group page */

h1 {
    font-family: "Open Sans Condensed";
    font-weight: bold;
}

.grouppage_join_area {
    background-color: rgba(0, 0, 0, 0);
}

.btn_blue_white_innerfade {
    border-radius: 15px 0px 0px 15px;
    margin-right: -2px;
}

.group_associated_game {
    background-color: rgb(30, 30, 30);
    font-family: "Open Sans Light";
    font-weight: normal;
}

.group_content.bottomless, .group_content.group_summary {
    background-image: none;
}

.grouppage_logo, .grouppage_logo img {
    border-radius: 50%;
}

.member_tile, .grouppage_membertiles_overlay {
    opacity: 0;
}

/* Friends list */

.profile_small_header_bg {
    background-image: none;
}

/* Steam Enhanced */

.hr {
    opacity: 0;
}

.complete {
    opacity: 0;
}
}
