css:
@-moz-document domain("zeppelin.apache.org") {
/* zeppelin dark editor fixes */
body {
  color:#ccc;
  background: #222;
}
.paragraphAsIframe .editor, .paragraph .editor {
  background: #222;
}
div.header {
    color:#666;
}

.paragraph-col {
  border: 2px solid #222;
}

.paragraph .editor {
  margin-top:6px;
  margin-bottom:2px;
  padding-bottom:14px;
  border-left: 4px solid #272727;
  border-top: 7px solid #272727;
  border-bottom: 7px solid #272727;
  background-color: #272727;
}

.btn-default.disabled.active, .btn-default.disabled:active, .btn-default[disabled].active, .btn-default[disabled]:active, fieldset[disabled] .btn-default.active, fieldset[disabled] .btn-default:active {
    background-color:#222;
    color:#777;
}

.navbar-inverse{
    background-color:#222;
	border-color:#222;
}

.btn-default .disabled {
    background-color:#333;
    color:#999;
}

.btn-default, .form-control, .input-group-btn, .tableDisplay .option .columns ul{
    background-color:#222;
    border-color:#222;
    color:#ccc;
}

.paragraph-col .focused{
  border-color:#333;
  background-color:#333;
}

.paragraph .control, .box, .well, .noteAction {
  color:#ccc;
  background: #222;
  border-color:#222;
}

.box:hover, .well:hover {
  color:#ddd;
  background: #333;
  border-color:#333;
}

/* ace monokai fixes */

.ace-chrome .ace_marker-layer .ace_bracket{
  margin: -1px 0 0 -1px;
  border: 1px solid rgba(255, 255, 255, 0.25);
}

.ace-chrome .ace_gutter {
  background: #272822;
  color: #8F908A
}

.nvd3-svg, .htCore {
    background-color:#aaa;
	color:#222;
}

.ace-chrome {
  background-color: #272822;
  color: #F8F8F2
}

.ace-chrome .ace_cursor {
  color: #F8F8F0
}

.ace-chrome .ace_marker-layer .ace_selection {
  background: #49483E
}

.ace-chrome .ace_paren{
    color:#87a6c6
}

.ace-chrome .ace_marker-layer .ace_bracket {
    margin: 0 2px 3px 0;
    position: absolute;
    background-color: rgba(255, 255, 0, 0.4);
}

.ace-chrome .ace_marker-layer .ace_selected-word {
    background-color: #75715E;
    border: 1px solid #49483E
}

.ace-chrome .ace_keyword.ace_operator {
  color: #F92672
}

/* ace monokai default */

.ace-chrome.ace_multiselect .ace_selection.ace_start {
  box-shadow: 0 0 3px 0px #272822;
}

.ace-chrome .ace_marker-layer .ace_step {
  background: rgb(102, 82, 0)
}

.ace-chrome .ace_marker-layer .ace_active-line {
  background: #202020
}

.ace-chrome .ace_gutter-active-line {
  background-color: #272727
}

.ace-chrome .ace_invisible {
  color: #52524d
}

.ace-chrome .ace_entity.ace_name.ace_tag,
.ace-chrome .ace_keyword,
.ace-chrome .ace_meta.ace_tag,
.ace-chrome .ace_storage {
  color: #F92672
}

.ace-chrome .ace_punctuation,
.ace-chrome .ace_punctuation.ace_tag {
  color: #fff
}

.ace-chrome .ace_constant.ace_character,
.ace-chrome .ace_constant.ace_language,
.ace-chrome .ace_constant.ace_numeric,
.ace-chrome .ace_constant.ace_other {
  color: #AE81FF
}

.ace-chrome .ace_invalid {
  color: #F8F8F0;
  background-color: #F92672
}

.ace-chrome .ace_invalid.ace_deprecated {
  color: #F8F8F0;
  background-color: #AE81FF
}

.ace-chrome .ace_support.ace_constant,
.ace-chrome .ace_support.ace_function {
  color: #66D9EF
}

.ace-chrome .ace_fold {
  background-color: #A6E22E;
  border-color: #F8F8F2
}

.ace-chrome .ace_storage.ace_type,
.ace-chrome .ace_support.ace_class,
.ace-chrome .ace_support.ace_type {
  font-style: italic;
  color: #66D9EF
}

.ace-chrome .ace_entity.ace_name.ace_function,
.ace-chrome .ace_entity.ace_other,
.ace-chrome .ace_entity.ace_other.ace_attribute-name,
.ace-chrome .ace_variable {
  color: #A6E22E
}

.ace-chrome .ace_variable.ace_parameter {
  font-style: italic;
  color: #FD971F
}

.ace-chrome .ace_string {
  color: #E6DB74
}

.ace-chrome .ace_comment {
  color: #75715E
}

.ace-chrome .ace_indent-guide {
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAACCAYAAACZgbYnAAAAEklEQVQImWPQ0FD0ZXBzd/wPAAjVAoxeSgNeAAAAAElFTkSuQmCC) right repeat-y
}
}
