css:
@-moz-document url-prefix("http://192.168.2.100:9001/") {
/* local twonky server - black & video fullscreen */

/* default */

body {
	background-color : rgb(0,0,0);
    color            : rgb(255,255,255);
}
.copyright {
	cursor : default;
}
a {
	color : rgb(255,255,255);
}
/* correction */
body > hr {
	border : 0px;
	width  : 0px;
}
}

@-moz-document url-prefix("http://192.168.2.100:9001/webbrowse") {
/* /webbrowse */

a.button-config:hover , 
a.button-home:hover , 
a.button-upload:hover , 
a.button-back:hover , 
a.button-next:hover ,
a.button-prev:hover , 
a.play:hover , 
a.container:hover , 
a.playcontainer:hover {
	background-color : rgb(255,255,255);
    color            : rgb(0,0,0);
}
.play ,
.playcontainer {
	color : rgb(255,255,255);
}
#title {
	color : rgb(255,255,255);
}

