css:
@-moz-document domain("v3rmillion.net") {
#wrapper {
    background: #76ee70;
    padding: 20px 0;
}

body {
    background: #76ee70;
    color: #ffffff;
    text-align: center;
    line-height: 1.4;
    margin: 0;
    font-family: Open Sans, Tahoma, Verdana, Arial, Sans-Serif;
    font-size: 13px;
    overflow-y: scroll;
}

#container {
    width: 90%;
    text-align: left;
    line-height: 1.4;
    margin: 0 auto;
    background-color: #76ee70;
}

.navigation {
    background: #404040;
    border: 2px solid #eeeeee;
    margin: auto auto 20px auto;
    padding: 10px;
    color: #fff;
    font-size: 15px;
    text-align: left;
}

#content {
    background: #404040;
    border: 2px solid #eeeeee;
    width: auto !important;
    padding: 20px;
    padding-bottom: 0px !important;
    margin-bottom: 40px;
    overflow: hidden;
}

.tcat {
    background: #76ee70;
    color: #eeeeee;
    padding: 6px 10px;
    font-size: 12px;
}

.thead {
    background: #41ab3c;
    color: #fff;
    padding: 10px;
}

.tfoot {
    padding: 10px;
    background: #41ab3c;
    color: #e0e0e0;
}

a.button:link, a.button:visited, a.button:active {
    background: #41ab3c;
    color: #fff;
    display: inline-block;
    padding: 4px 8px;
    margin: 2px 2px 6px 2px;
    border: 1px solid #eeeeee;
    font-size: 14px;
    font-weight: 700;
}

.pagination a {
    background: #41ab3c;
    border: 1px solid #eeeeee;
    color: #fff;
}

.inline_row a:visited {
    color: #41ab3c;
    font-size: 90%;
}

.inline_row a {
    color: #76ee70;
    font-size: 90%;
}

#footer {
    position: relative;
    background: #101010;
    border-top: 5px solid #76ee70;
    color: #fff;
    clear: both;
}

button, input.button {
    background: #41ab3c;
    border: 1px solid #eeeeee;
    padding: 5px 8px;
    color: #fff;
    font-size: 14px;
    font-family: Open Sans, Tahoma, Verdana, Arial, Sans-Serif;
    cursor: pointer;
    outline: 0;
}

.cRem a:visited {
    color: #41ab3c !important;
}
.cRem a:link {
    color: #41ab3c !important;
}

Style Attribute {
    color: #76ee70;
}

.trow1, .trow2 {
    background: #202020;
    border-bottom: 1px solid #303030;
}

#bridge {
    background: rgb(64, 64, 64);
    height: 45px;
    border-bottom: 5px solid #41ab3c;
    border-top: 5px solid #41ab3c;
}

ul.menu li a.active {
    background: #41ab3c !important;
    color: #fff !important;
}

ul.menu li ul {
    z-index: 9999;
    display: none;
    position: absolute;
    top: 45px;
    background: #404040;
    width: 180px;
    overflow: hidden;
    margin: 0;
    padding: 10px 0px;
    border-top: 5px solid #76ee70;
}

ul.menu li ul li a:link, ul.menu li ul li a:visited {
    display: block;
    float: none !important;
    background: #404040;
    padding: 10px 15px;
    border: 0;
    font-size: 12px;
}

.usercp_nav_item:before {
    font-size: 16px;
    font-family: 'FontAwesome';
    color: #41ab3c;
    width: 28px;
    margin-right: 5px;
    text-align: center;
    display: inline-block;
}

Style Attribute {
    color: #41ab3c;
}

.panel_buttons {
    z-index: 9999;
    display: none;
    position: absolute;
    top: 45px;
    background: #404040;
    width: 180px;
    overflow: hidden;
    margin: 0;
    padding: 10px 0px;
    border-top: 5px solid #76ee70;
    text-align: left;
}

.forum_on {
    color: #41ab3c;
}

.thread_head {
    background: #1a1a1a;
    width: 100%;
    border-bottom: 5px solid #76ee70;
}

.postbit_buttons > a:link, .postbit_buttons > a:hover, .postbit_buttons > a:visited, .postbit_buttons > a:active {
    display: inline-block;
    padding: 5px;
    margin: 2px;
    font-size: 12px;
    background: #41ab3c;
    border: 1px solid #eeeeee;
    color: #fff;
    font-weight: 700;
}

.buttons:hover {
    background-color: #76ee70;
    color: white;
}
.buttons {
    background-color: #202020;
    border: 1px solid #76ee70;
    padding: 6px;
    display: inline-block;
    border-radius: 3px;
}

.footer {
    width: 100%;
    background-color: #41ab3c;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    padding: 5px;
}

.wrap a {
    -moz-transition: all .2s ease-in;
    -o-transition: all .2s ease-in;
    -webkit-transition: all .2s ease-in;
    transition: all .2s ease-in;
    color: #76ee70;
    text-decoration: none;
}

.wrap a:hover {
    color: #cd1818;
}
}
