.pubd-login .error-msg-list
{
	list-style-type: none;
	padding-left: 0;
	margin: 8px auto;
}
.pubd-login .error-msg-list-item
{
	width: 280px;
	background-color: rgba(0,0,0,.3);
	margin: 0 auto;
	margin-bottom: 2px;
	padding: 13px 10px;
