	border-radius: 4px;
}
.pubd-login .submit
{
	display: block;
	margin: 0 auto;
	margin-bottom: 8px;
	width: 300px;
	height: 40px;
	line-height: 40px;
	background-color: #25c6ff;
	color: #fff;
	font-weight: 700;
	outline: none;
	cursor: pointer;
	text-align: center;
	text-decoration: none;
	border: none;
	border-radius: 4px;
}
.pubd-login .signup-form-nav
{
	list-style: none;
	width: 300px;
	margin: 0 auto;
	color: #666;
	font-size: 12px;
	overflow: hidden;
}
/*▲登陆对话框内容*/
/*▼下载当前画师窗口内容*/
.pubd-downthis
{/*下载当前画师*/
	left: calc(50vw - 240px);
	top: 180px;
	width: 400px;
}
.pubd-downthis .pubd-icon
{/*选项对话框图标*/
	background: url('https://material.io/tools/icons/static/icons/baseline-get_app-24px.svg');
}
.pubd-dialog label
{/*下载内容单选框*/
	display: inline-block;
}
.pubd-downthis .pubd-down-content
{/*下载内容单选框*/
	width: 50%;
}
.pubd-downthis dt
{/*中断按钮*/
	position: relative;
}
.pubd-downthis .pubd-breakdown
{/*中断按钮*/
	position: absolute;
	bottom: 0;
	right: 0;
	padding: 0;
	height: 19px;
	line-height: 11px;
	font-size:12px;
}
.pubd-downthis .pubd-downthis-log
{/*下载日志*/
	min-width: 100px;
	min-height: 40px;
	width : calc(100% - 10px);
	height : 100px;
resize:none;
}
.pubd-downthis .pubd-downthis-downbar
{
}
.pubd-downthis .pubd-textdown
{/*文本下载按钮*/
	width: calc(20% - 5px);
	height: 50px;
	margin-right: 5px;
	vertical-align: middle;
}
.pubd-downthis .pubd-startdown
{/*开始下载按钮*/
	width: 80%;
	height: 50px;
	font-size: 30px;
	vertical-align: middle;
}
.pubd-downthis .pubd-downthis-textout-bar
{
	height : calc(100% - 400px);
}
.pubd-downthis .pubd-downthis-textout
{/*文本输出*/
	min-width: 100px;
	min-height: 40px;
	width : calc(100% - 10px);
	height : 100%;
	resize:none;
}
