css:
/* i really want this to be global */

body {
    background: #f7f7f7;
}

header.main {
    display: none;
}
#puzzles {
    display: none;
}
footer {
    display: none;
}
div.admin {
    float: right;
    margin-top: 50px;
    margin-right: 0;
}
#user {
    margin-top: -200px;
}
#user > h3:nth-child(4),
#user > h1 {
    display: none;
}
#user,
div#times,
div#chart-container,
p.tabs,
div#chart,
div#highcharts-0.highcharts-container,
div#highcharts-0.highcharts-container svg,
div#highcharts-0.highcharts-container defs,
div#highcharts-0.highcharts-container rect {
    margin-left: 50px;
    width: 100%;
}
a,
#content a,
#content a:visited,
div.fancybox-inner a,
div.fancybox-inner a:visited,
a,
#content a,
#content a:visited,
div.fancybox-inner a,
div.fancybox-inner a:visited,
a,
#content a,
#content a:visited,
div.fancybox-inner a,
div.fancybox-inner a:visited {
    color: #f2635d;
}
nav.main ul li.session.selected a,
nav.main ul li.session a:hover {
    color: #f2635d;
}
nav.main {
    background: #02212d;
    background: #143040 url(http://www.raveisescapes.com/app/themes/raveis/dist/images/closeX-dark.svg) center no-repeat;
    border-top: 0px solid #00385B;
    border-bottom: 1px solid #fff;
}
nav.main ul li a {
    color: #fff;
}
nav.main ul li a:hover,
nav.main ul li.selected a,
#content h1,
div.fancybox-inner h1 {
    background: none;
    color: #f2635d;
    border-top: 0;
    border-bottom: 2px solid #f2635d;
}
#content a:hover,
#content a:active,
div.fancybox-inner a:hover,
div.fancybox-inner a:active {
    background: none;
}
#wca {
    float: left;
    padding-bottom: 50px;
}
#user h3:first-child {
    display: none;
}
#content #user #wca,
div.fancybox-inner #user #wca {
    margin: 0 0 2em 0;
    text-align: left;
}
#content #user #wca a,
div.fancybox-inner #user #wca a {
    float: left;
    font-size: 2.25em;
    line-height: 2em;
}
#content a:hover,
#content a:active,
div.fancybox-inner a:hover,
div.fancybox-inner a:active {
    background: none;
}
#kinds {
    background: #efefef;
    display: none;
}
#subnavigation {
    border-top: 0px solid #535353;
    background: #f0f0f0;
}
#subnavigation li a,
#timer > div.time-container > p {
    color: #bfbfbf;
}
#kinds #subnavigation li a:hover,
#kinds #subnavigation li.checked a,
#subnavigation li a:hover,
#subnavigation li.checked a {
    color: #00385B;
}
#content {
    margin-left: -200px;
    color: #3a3a3a;
    padding-top: 200px;
    background: #f7f7f7;
}
#content h1 { color: #333;
line-height: 1.5em;
}
#content h1,
#content h2,
#content h3,
#content h4,
#content h5,
#content h6,
#content h3,
div.fancybox-inner h3 {
    color: #333333;
}
#content,
div.fancybox-inner {
    color: #3a3a3a;
}
#content #porno #timer .scramble {
    width: 93%;
    background: #fafbff;
    color: #555353;
    font-size: 3.2em;
    position: absolute;
    top: 12%;
    left: 1%;
    padding: 40px;
    border: 1px solid #cccccc;
    -webkit-box-shadow: 0 8px 6px -6px #555353;
    -moz-box-shadow: 0 8px 6px -6px #555353;
    box-shadow: 0 8px 6px -6px #555353;
}
#content #porno #timer,
div.fancybox-inner #porno #timer,
section#stats {
    width: 50%;
}
#content #porno #stats .current,
div.fancybox-inner #porno #stats .current {
    margin-bottom: 1.8em;
}
#content #porno #timer,
div.fancybox-inner #porno #timer,
#content #porno #stats,
div.fancybox-inner #porno,
#content #times #singles,
div.fancybox-inner #times #singles {
    background: #f7f7f7;
    border-radius: 0;
}
#content #porno #timer div.add_comment div.bubble,
div.fancybox-inner #porno #timer div.add_comment div.bubble {
    color: #1b8bb8;
}
#content #user ul.records li,
div.fancybox-inner #user ul.records li {
    background: #fafafa;
    background: #fafafa;
    border: 2px solid #efefef;
    padding: 13px;
    margin-left: 2px;
    margin-right: 0px;
}
#content #user ul.records .record {
    width: 22%;
}
#content #user ul.records thead,
div.fancybox-inner #user ul.records thead {
    border-bottom: 1px solid #ccc;
}
::selection {
    background-color: #f2635d;
}
#content #user h1 small,
div.fancybox-inner #user h1 small {
    color: #6f6f6f;
}
nav.main ul li a:hover,
nav.main ul li.selected a,
#content h1,
div.fancybox-inner h1 {
    border-bottom: 2px solid #f2635d;
}
#content #user #wca a.logo,
div.fancybox-inner #user #wca a.logo {
    padding: 5px 25px;
    margin-right: 0.2em;
}
#content h3,
div.fancybox-inner h3 {
    color: #535353;
}
#content #user div.admin,
div.fancybox-inner #user div.admin {
    float: right;
    margin-top: 2em;
    padding-right: 2.8em;
}
#content #user div.admin a {
    color: #6f6f6f;
}
#content #porno #stats h4,
div.fancybox-inner #porno #stats h4 {
    color: #333333;
}
#timer > div.time-container > div {
    color: #3C97CC;
}
#flash {
    background: #ffea36;
    border-bottom: 1px solid #00385b;
    margin-top: -3.3em;
    padding: 1.25em;
}
#flash strong {
    font-size: 1.2em !important;
}
#flash a {
    color: #000;
    font-size: 1.2em !important;
    padding: 20px 20px;
    text-decoration: underline;
}
#flash a.close {
    padding: 5px;
    text-decoration: none;
}
div.flash a.close {
    background: url(http://www.raveisescapes.com/app/themes/raveis/dist/images/closeX-dark.svg) center no-repeat;
}
div.flash a.close:hover {
    background: #143040 url(http://www.blessthefallmusic.com/images/close-x.svg) center no-repeat;
}
div#flash:hover {
    background: #ffea36 url(http://www.raveisescapes.com/app/themes/raveis/dist/images/closeX-dark.svg) center no-repeat;
}
div#flash:hover a {
    color: #fff;
    background: #000;
    padding: 20px 20px;
}
div#flash:hover a.close {
    background: url(http://www.raveisescapes.com/app/themes/raveis/dist/images/closeX-dark.svg) center no-repeat;
    padding: 5px;
}
#timer > div.add_comment > div,
#content #porno #timer div.add_comment div.bubble,
div.fancybox-inner #porno #timer div.add_comment div.bubble {
    color: #fff;
    background: #3C97CC;
    padding: 1em;
}
#content #porno #timer div.add_comment div.bubble a,
div.fancybox-inner #porno #timer div.add_comment div.bubble a,
div.flash p {
    color: #000;
    font-size: 1.8em;
}
#content #times #singles,
div.fancybox-inner #times #singles {
    position: absolute;
    top: 300px;
    right: 30px;
    padding-left: 20px;
    padding-right: 20px;
    background: #e5e5e5
}
#content .center,
div.fancybox-inner .center {
    /* width:1140px; */
    
    width: 100%;
}
#content .time.dnf,
div.fancybox-inner .time.dnf {
    color: #8c8c8c;
}
