css:
@-moz-document domain("futbin.com") {
html,
body {
    background: #333;
    color: #888;
}

.box_price {
    background: #111;
}

.versions-area {
    background: #252525;
}

.full-width-g {
    background: #222;
}

.player_name_players_table {
    color: #ddd;
}

.player_tr_2:hover,
.table-hover tbody tr:hover>td,
.table-hover tbody tr:hover>th {
    background: #000 !important;
}

.table td {
    color: #aaa;
}

.m_box {
    background: #111;
}

.icon-desc {
    color: #fff;
}

.full-width-gs {
    background: #111;
}

.stat_holder_sub {
    color: #b6e6fd;
}

.stat_holder_main {
    color: #fff;
}

.chem_style_dd>div {
    background: #252525;
}

.chem_style_dd>div>a {
    color: #fff;
}

#info_content,
.table-info>tbody>tr>th,
.table-info>tbody>tr>td {
    background: #111;
    color: #aaa;
}

.table-row-text {
    color: #aaa !important;
}

.header_mid>ul>li>a {
    color: #6db6ff !important;
}

.link-holder>a {
    color: #aaa;
}

#link-btn.active {
    color: #fff;
}

.link-holder>a:hover {
    color: #eee;
}

.links-overlay {
    background: #111;
}

#highcharts-0,
#highcharts-0 svg,
#highcharts-0 svg rect {
    background: #111;
    fill: #222;
}

.nav-tabs>.active>a,
.nav-tabs>.active>a:focus,
.nav-tabs>.active>a:hover {
    color: #fff;
}

.nav-tabs>li>a {
    color: #999;
}

.top-row,
.no_results {
    background: #111;
}

.table td {
    background: #222;
    border-color: #444;
}

.table-striped tbody > tr:nth-child(2n+1) > td,
.table-striped tbody > tr:nth-child(2n+1) > th {
    background-color: #111;
}

.breadcrumb>div.active {
    color: #aaa;
}

.player_header {
    color: #fff;
}

.mid-p-nav-platform-selector {
    background: rgba(0, 0, 0, 0.2);
}

.mid-p-nav-blur {
    border-color: #444;
}

.top-title {
    color: #fff;
}

.full-width-gb {
    background: #111;
}

.reviews-small-block {
    background: #252525;
    color: #999;
}

.reviews-small-block p {
    color: #999;
}

.uneditable-input,
input[type=text],
input[type=password],
input[type=datetime],
input[type=datetime-local],
input[type=date],
input[type=month],
input[type=time],
input[type=week],
input[type=number],
input[type=email],
input[type=url],
input[type=tel],
input[type=color],
input[type=search],
textarea,
.ql-editor,
.ql-toolbar.ql-snow {
    background: #000;
    color: #fff;
    border-color: #444 !important;
}

.ql-editor p,
.range-value,
.reviews-title {
    color: #fff;
}

.ql-container.ql-snow {
    border-color: #444 !important;
}

.select2-container--default .select2-selection--single,
.select2-dropdown {
    background: #000;
    border-color: #444 !important;
}

.close-review-form {
    color: red;
}

.select2-container--default .select2-selection--single .select2-selection__rendered {
    color: #fff;
}

.select2-results__option {
    color: #fff !important;
}

.reviews-small-block>.review-holder>.review-text>.review-inner>.review-ratings>.review-attr-block>.review-attr-holder>.review-attr-value,
.reviews-small-block>.review-holder>.review-title-block>.review-title,
.reviews-small-block>.review-holder>.review-text>.review-inner>.review-ratings>.review-attr-block>.review-attr-holder>.review-attr-text,
.progress-holder>.progress-attr-text {
    color: #96b4c2;
}

.tags div {
    background: #2895e5;
    color: #fff;
    border-color: #74ffcf;
}
}
