css:
@-moz-document regexp('^.*fit-wiki\.cz(.*[^p].{2}|.*.{0,1}[^d].{1}|.*.{0,2}[^f]|.{0,4})$') {
	/*---------------ZAKLADNI---------------*/

	body {
		max-width: 1000px;
		text-align: center;
		font-family: "Roboto", "Fira Sans", Arial, sans-serif;
		margin: 20px auto;
	}

	/* POZADI */
	body,
	div.dokuwiki div.comment_wrapper, /* diskuze */
	div.dokuwiki .bar, div.dokuwiki .breadcrumbs { /* hlavicka */
		background-color: #181821 !important;
	}

	div.dokuwiki div.ace-doku,
	html>body div.dokuwiki textarea.edit, /* pridavani komentare do diskuze */
	div#dw__toc, /* obsah */
	div.dokuwiki table.inline, /* tabulka */
	.dokuwiki .wrap_box { /* widgety */
		background-color: #131315;
	}

	div.dokuwiki div.section_highlight,
	div.hiddenHead p, /* zahlavi - rozbalovaci texty - zadane testy atd. */
	div.dokuwiki div.comment_buttons input.button, /* tlacitka v diskuzi */
	div.dokuwiki input.button, div.dokuwiki button.button,
	button, /* tlacitka */
	div.dokuwiki input.edit, /* vyhledavaci policko */
	div.dokuwiki table.inline th { /* zahlavi tabulky */
		background-color: #1c1c1c !important;
	}

	img.math, img.mediacenter {
		padding: 3px;
		background-color: #fbe;
	}

	strong {
		color: #bbb;
	}

	/* BARVA TEXTU */

	body,
	html>body div.dokuwiki textarea.edit, /* pridavani komentare do diskuze */
	div.dokuwiki input.edit, /* vyhledavaci policko */
	div.dokuwiki table.inline { /* tabulka */
		color: #bbb !important;
	}

	div.dokuwiki .bar, div.dokuwiki .breadcrumbs, /* hlavicka */
	div#dw__toc, /* obsah */
	button, /* tlacitka */
	div.hiddenHead p, /* zahlavi - rozbalovaci texty - zadane testy atd. */
	div.dokuwiki input.button, div.dokuwiki button.button,
	div.dokuwiki div.comment_buttons input.button, /* tlacitka v diskuzi */
	div.dokuwiki table.inline th, /* zahlavi tabulky */
	.dokuwiki .wrap_box { /* widgety */
		color: #ff99de;
	}

	div.dokuwiki p {
		color: #ccd;
	}

	/* OKRAJE */
	.dokuwiki div.wrap_round {
		border-radius: 0; /* zadne kulate okraje */
	}

	div.hiddenHead p, /* zahlavi - rozbalovaci texty - zadane testy atd. */
	div.hiddenGlobal { /* rozbalovaci texty - zadane testy atd. */
		border-color: #bbc;
	}

	html>body div.dokuwiki textarea.edit, /* pridavani komentare do diskuze */
	div.dokuwiki input.edit, /* vyhledavaci policko */
	div.dokuwiki input.button, div.dokuwiki button.button,
	div.dokuwiki div.comment_buttons input.button, /* tlacitka v diskuzi */
	button { /* tlacitka */
		border-color: #ff99de;
	}

	html>body div.dokuwiki textarea.edit, /* pridavani komentare do diskuze */
	div.hiddenHead p, /* zahlavi - rozbalovaci texty - zadane testy atd. */
	div.hiddenGlobal, /* rozbalovaci texty - zadane testy atd. */
	div.dokuwiki input.edit, /* vyhledavaci policko */
	div.dokuwiki input.button, div.dokuwiki button.button,
	div.dokuwiki div.comment_buttons input.button, /* tlacitka v diskuzi */
	button { /* tlacitka */
		border-style: solid;
		border-width: 1px;
	}

	/* OSTATNI */

	html>body div.dokuwiki textarea.edit, /* pridavani komentare do diskuze */
	div.dokuwiki div.comment_buttons input.button, /* tlacitka v diskuzi */
	div.dokuwiki input.button, div.dokuwiki button.button,
	button, /* tlacitka */
	div.dokuwiki input.edit { /* vyhledavaci policko */
		-webkit-appearance: none;
		background: none !important;
		padding: 2px;
	}

	/*---------------NADPISY---------------*/

	.dokuwiki h1 {
		color: #f59 !important;
	}

	div.dokuwiki h2 a, /* nadpis Diskuze */
	.dokuwiki h2 { /* ostatni nadpisy */
		color: #f7a !important;
	}

	.dokuwiki h3 {
		color: #e6a !important;
	}

	.dokuwiki h4 {
		color: #8df !important;
	}

	/*----------ODRAZKOVANY TEXT-----------*/

	.li {
		color: #fbe !important;
	}

	/*---------------ODKAZY----------------*/

	div.dokuwiki a:link {
		color: #f9d !important;
	}

	div.dokuwiki a:hover {
		color: #f59 !important;
	}

	div.dokuwiki a:visited {
		color: #f59 !important;
	}

	div.dokuwiki a.urlextern:link {
		color: #e7b !important;
	}

	/* odkaz na existujici stranku */
	div.dokuwiki a.wikilink1 {
		color: #fce !important;
	}

	/* odkaz na NEexistujici stranku */
	div.dokuwiki a.wikilink2 {
		color: #f36 !important;
		border-bottom: dashed 1px !important;
	}
}
