css:
@-moz-document domain("www.cnews.cz") {
body, .td-header-menu-wrap, .td-banner-wrap-full, .td-header-menu-wrap-full, .td-main-content-wrap, .td-category-grid, .td-category-header, #rss_hub {
    background: #000;
}
article {
    padding: 10px;
}
.td-main-logo img {
    filter: brightness(100) invert(1);
}
.podminky {
    display: none !important;
}
.comments, .wp-caption-text, header .td-post-comments a {
    color: #bbb;
}
cite, .td-post-next-prev-content a, .td-author-name a, .td_category_template_4 .td-category-siblings .td-category a {
    color: #ddd;
}
#respond h3, .logged-in-as a, h1 > a, h2 > a, h3 > a, h4 > a, h5 > a, h6 > a, h1, h2, h3, h4, h5, h6, .td-page-content, .td-post-content, .wpb_text_column, .td_block_text_with_title, .woocommerce #tab-description, .mce-content-body, .td-post-next-prev-content a, #cancel-comment-reply-link {
    color: #eee;
}
.td_module_6 .entry-title a, .td_module_6.td_module_wrap:hover .entry-title a, .td-post-author-name a, .td-post-author-name, header .td-post-author-name, header .td-post-date, .td-post-next-prev-content a {
    color: #fff;
}
.post, .td-main-content-wrap .td-container {
    background: #111;
    color: #eee;
}
.td-post-content td,
.td-page-content .twitter-tweet p, .td-post-content .twitter-tweet p, .wpb_text_column .twitter-tweet p, .td_block_text_with_title .twitter-tweet p, .bbpress .twitter-tweet p, .woocommerce #tab-description .twitter-tweet p, .mce-content-body .twitter-tweet p, .comment-content .twitter-tweet p {
    color: #aaa;
}
.td-post-small-box a {
    color: inherit;
}
.td-module-comments a, .td-smart-list-dropdown-wrap .td-smart-list-dropdown {
    background: #333;
}
.td-module-comments a:after {
    border-color: #333 transparent transparent transparent;
}
.plus td {
    color: #0f0;
}
.minus td {
    color: #f00;
}
.white-bck {
    background: #fff;
    filter: invert(1);
}
.td-main-content-wrap > div {
    background-color: #111 !important;
}
.td-main-page-wrap > div {
    background-color: #111 !important;
}
ins {
    display: none !important;
}
/*[[textarea-col]]*/

/* imgs fix when js is disabled */
body.td-animation-stack-type0 .td-animation-stack .entry-thumb, body.td-animation-stack-type0 .post img {
    opacity: 1;
}
}
