css:
@-moz-document domain("admin.staging.experticity.com"), domain("admin.experticity.com") {
.cm-s-default .CodeMirror-gutters {
    background: #3f3f3f !important;
}
.cm-s-default .CodeMirror-foldgutter-open,
.CodeMirror-foldgutter-folded {
    color: #999
}
.cm-s-default .CodeMirror-cursor {
    border-left: 1px solid #fff !important;
}
.cm-s-default {
    background-color: #313134;
    color: #dcdccc
}
.cm-s-default span.cm-builtin {
    color: #dcdccc;
    font-weight: 700
}
.cm-s-default span.cm-comment {
    color: #7f9f7f
}
.cm-s-default span.cm-keyword {
    color: #f0dfaf;
    font-weight: 700
}
.cm-s-default span.cm-atom {
    color: #bfebbf
}
.cm-s-default span.cm-def {
    color: #dcdccc
}
.cm-s-default span.cm-variable {
    color: #dfaf8f
}
.cm-s-default span.cm-variable-2 {
    color: #dcdccc
}
.cm-s-default span.cm-string {
    color: #cc9393
}
.cm-s-default span.cm-string-2 {
    color: #cc9393
}
.cm-s-default span.cm-number {
    color: #dcdccc
}
.cm-s-default span.cm-tag {
    color: #93e0e3
}
.cm-s-default span.cm-property {
    color: #dfaf8f
}
.cm-s-default span.cm-attribute {
    color: #dfaf8f
}
.cm-s-default span.cm-qualifier {
    color: #7cb8bb
}
.cm-s-default span.cm-meta {
    color: #f0dfaf
}
.cm-s-default span.cm-header {
    color: #f0efd0
}
.cm-s-default span.cm-operator {
    color: #f0efd0
}
.cm-s-default span.CodeMirror-matchingbracket {
    box-sizing: border-box;
    background: transparent;
    border-bottom: 1px solid
}
.cm-s-default span.CodeMirror-nonmatchingbracket {
    border-bottom: 1px solid;
    background: none
}
.cm-s-default .CodeMirror-activeline {
    background: #000
}
.cm-s-default .CodeMirror-activeline-background {
    background: #000
}
.cm-s-default div.CodeMirror-selected {
    background: #545454
}
.cm-s-default .CodeMirror-focused div.CodeMirror-selected {
    background: #4f4f4f
}
.alert,
.alerts {
    background-color: #3f3f3f;
    font-weight: 500;
    color: #ccc;
}
iframe#trainingPagePreview{
    width: 960px !important;
    height: 540px !important;
}
#moduleCommentsHeader .summarySection {
    display: block !important;
}
}
