css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lnb.fr") {
html,#content{
	background:#222 !important;;
}
	.grid-2{
		padding: 0;
		background:#333;
	}
	.grid-3{
		
		background: #333;
	}
	.grid-4{
		background: #333;
	}
	#content{
		color:#ccc;
	}
	#breadcrumb{
		background: #333;
	}
	#breadcrumb a{
		color :#4078c0;
	}
	.block{
		background: #333;
		border: 1px solid gray;
	}
	.block .inner{
		max-height: 310px
	}
	
	
	/* Menu */
	.menu-first, #nav-main{
		background: #333;
		border-bottom:1px solid gray;
	}
	#nav-main > ul span{
		color: #ccc;
	}
	#account .login, #account .register{
		color: #ccc;
	}
	#nav-teams-list img{
			filter:none;
		}
	/* End Menu */
	
	/* Video */
	.tv-list .video{
		background: #444;
	}
	.tv-list .video a{
		border:1px solid gray
	}
	.tv-list .video time{
		color:#ccc;
	}
	.tv-list .video h2{
		color:#4078c0;
	}
	/* end video */
	
	/* Scoreboard */
	.block > header{
		padding: 10px 0;
	}
	#scoreboard img{
		display: none;
	}
	
	#scoreboard .tabs-matchs{
		margin:0px 0;
		background: url(http://s.lnb.fr/lnb/file/201409/pro-background.png) 10px 10px no-repeat;
		border: 0;
	}
	#scoreboard table{
		border:0px;
	}
	#scoreboard-a{
		background: #333;
	}
	#scoreboard .nav-tab2 a{
		background: #333;
	}
	#scoreboard .calendar th{
		background: #333;
	}
	#scoreboard .calendar td{
		background: #444;	
	}
	th, td{
		border-top:1px solid gray;
	}
	th{
		border:1px solid gray;
	}
	
	#scoreboard .footer,#scoreboard header{
		background: #4078c0A0;		
	}
	td.score.final{
		padding:4px;
	}
	/* End scoreboard */
	
	/* Programs */
	#programs li{
		background: #444;
	}
	#programs h2{
		color: gray;
	}
	/* end programs */
	
	/* Result */
	#results .main{
		background: #444;
		
	}
	.nav-tab2{
		border: 1px solid gray;
		
	}
	#results .boxscore{
		background: #444;
	}
	#results .details-container{
		background: #333;
		
	}
	#results .boxscore .score span{
		background: #333;
		border: 1px solid gray;
	}
	.nav-tab2 li + li{
		border: 1px solid gray;
		
	}
	/* En result */
	
	/* Stats */
	
	/* MVP */
	#results .mvp tbody td{
		background-color: #444;
		color: #ccc;
	}
	#results .mvp thead th{
		background-color: #333;
	}
	/* End MVP */
	
	/* Movie */
	#movie table tr.q-away{
		background-color: #333;
	}
	#movie table tr.q-home{
		background-color: #4078c080;
		
	}
	/* end movie */
	
	#stats .stats-points .stats-table{
		background: #444 !important;
	}
	#stats .stats-graph tr.alternate-row{
		background-color: #333; 
	}
	#stats .stats-graph tr{
		background-color: #444; 
	}
	table .alt1, table .alt1 a, table .alt1 a:visited{
	background: #3B5797;	
	}
	a, a:visited{
	color: #4078c0;	
	}
	
	caption,th{
		background: #333;
	}
	tfoot th[scope="row"], tfoot td, .subtotal th[scope="row"], .subtotal td{
		background: #333;
		
	}
	#stats .stats-table .top-stats th{
		background: #333;
		
	}
	#stats .stats-graph{
		background: #444;
		
	}
	#resume .block, #stats .block, #movie .block, #LNBpics .block{
		background: #444;	
	}
	tbody tr:nth-child(2n){
		background: #333;
		
	}
	/* End stats*/
	
	/* player */
	#player .details{
		background: #444;
	}
	.nav-tab{
		background: #333;
	}
	.nav-tab .active, .nav-tab .active:visited, .nav-tab .active:hover, .nav-tab .active:active{
		background: #444;
		color:#ccc;
	}
	.nav-tab a:focus, .nav-tab a:hover, .nav-tab a:active{
		background: #444;
		
	}
	form fieldset{
		background: #444;
		
	}
	/* end player */

	/* News */
	.news-list .inner li, .news-list footer{
		background: #444;
		
	}
	/* end news */

	/* Billet */
	#billetterie .match{
		background: #444;
	}
	#billetterie .teams .names{
		color: #4078c0;
	}
	/* end billet */
	
	/* Add */
	#ads-square-rotation,
	#footer .partners-site,
	iframe#ad_iframe,
	iframe#google_ads_frame2,
	ins.adsbygoogle,
	div#adContent,
	div#skin-links,
	img#img15011480{
		display: none!important;
		visibility: collapse!important;
		width: 0px;
		height: 0px
	}
	/* end adds */
}
