css:
@-moz-document domain("dynalist.io") {
/* no indent */

.Node .Node .Node {
  padding-left: 0;
}
.Node-children {
  padding-left: 0px;
  border-left: none;
}
.Node.is-currentRoot > .Node-self .Node-content, .Node.is-currentRoot > .Node-self .Node-renderedContent {
    margin-left: 23px;
}
.Node-self.is-noteEmpty .Node-content, .Node-self.is-noteEmpty .Node-renderedContent, .Node-note, .Node-renderedNote {
    padding-bottom: 1em;
}

/* bullet */
.Node-bullet:before {
  content: none;
}
}
