style_setting_options.value:
/* shrinkthumbs yes */
.torpedo-container .thumb {max-width:150px !important; max-height:150px !important;}
.torpedo-container>.thumb>.torpedo-thumb-link>img, .torpedo-container .thumb.horizontal-crop .torpedo-thumb-link > img, .torpedo-container .thumb.vertical-crop .torpedo-thumb-link > img {width:unset; height:unset; max-width:150px !important; max-height:150px !important;}
.torpedo-container .thumb .info {padding: 10px;}
.torpedo-container-sm.torpedo-container .thumb.freeform .info {padding: 10px;}
.torpedo-container .thumb.freeform:not(.with-state-msg) .info {bottom: 0px; left: 0px; width:unset;}
.torpedo-container .thumb .info .title-wrap {font-size:16px;margin:0px;}
.torpedo-container .thumb .info .extra-info {font-size:12px;}

/* recolor fav and fade */
.torpedo-container .thumb .saved-faved-corner:hover {color: #c9d626; background: url("http://ic.pics.livejournal.com/aniretakss/49478799/18034/18034_original.png");}
.torpedo-container .thumb .saved-faved-corner {background: url("http://ic.pics.livejournal.com/aniretakss/49478799/18383/18383_original.png"); color: #dae5d6;}
.torpedo-container .thumb.faved .saved-faved-corner,.torpedo-container .thumb.faved .saved-faved-corner:hover {background: url("http://ic.pics.livejournal.com/aniretakss/49478799/18520/18520_original.png");}
.torpedo-container .thumb .info {background: linear-gradient(rgba(48, 64, 48, 0) 0px, rgba(48, 64, 48, 0.75) 100%) repeat scroll 0% 0% transparent;}

/* hide fav and stats */
.torpedo-container .thumb .saved-faved-corner {display: none !important;}
.torpedo-container .thumb .info .stats {display: none !important;}
