css:
@-moz-document domain("dynalist.io") {
/* general */

.DocumentContainer,
.AppContainer,
.AppContainer.is-borderShowing.is-globalSearch .DocumentContainer {
  background: #f5f5f5;
}
/* no indent */

.Node .Node .Node {
  padding-left: 0;
}
.Node-children {
  padding-left: 0px;
  border-left: none;
}
/* Card */

.Node-childrenInner .Node-outer {
  box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.2), 0 1px 5px 0 rgba(0, 0, 0, 0.12);
  margin-bottom: 10px;
  background: #fff;
  font-weight: bold;
}
/* Card body */

.Node-children .Node-childrenInner .Node-outer .Node-children .Node-childrenInner .Node-outer {
  border: none;
  border-top: 1px solid #ddd;
  border-radius: 0;
  margin-bottom: 0px;
  box-shadow: none;
  font-weight: normal;
}
/* bullet */

.Node-bullet:before {
  top: 0px;
}
.Node-bullet:before {
  content: none;
}
.is-collapsing .Node-bullet:before,
.is-parent .Node-bullet:before {
  content: '\e90d';
}
}
