@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("oconfronto.kinghost.net") {
  body{background-image: none !important;background-color:black!important;color:#006600!important;font-family: Verdana !important; font-size: 8pt !important;}
  img[src*="topo.jpg"] {display: none !important; }
  img[src*="refresh.jpg"] {display: none !important; }
  img[src*="glossyback.jpg"] {display: none !important; }
  img[src*="glossyback2.jpg"] {display: none !important; }
  img[src*="black_point.jpg"] {display: none !important; }
  img[src*="pin.jpg"] {display: none !important; }
  img[src*="arrow.gif"] {display: none !important; }
  #footer-text {background: none !important;}
  #wrapper {background: none !important;border-left:black!important;border-right:black!important;}
  #content {border-left:black!important;border-right:black!important;}
  fieldset {background-color:black!important; border-left:black!important;border-right:black!important;border-top:black!important;border-bottom:black!important;}
  .left-section { background: none !important;color:#CC0000!important;}
  .row1{background-color:black!important;}
  .row2{background-color:black!important;}
  .avatar {background-image: none !important;width: 0px!important;height: 0px!important;padding-top: 0px!important;padding-left: 0px!important;}
  #baixo-text {background-color:black!important;color:white!important;}
  a:link, a.menu1:active, a.menu1:visited  { color:#CCCCCC!important;  }
  a:hover { color:white!important; }
html,

	body,

	span,

	div,

	td,

	br,

	input,

	font,

	button,

	select,

	option {

		font-family: Verdana !important;

		font-size: 8pt !important;

	}



}
