css:
@-moz-document
regexp('^.*edux\.fit\.cvut\.cz(.*[^p].{2}|.*.{0,1}[^d].{1}|.*.{0,2}[^f]|.{0,4})$'),
regexp('^.*e-learning\.fit\.cvut\.cz(.*[^p].{2}|.*.{0,1}[^d].{1}|.*.{0,2}[^f]|.{0,4})$') {
	body {
		max-width: 1000px;
		text-align: center;
		font-family: "Roboto", "Fira Sans", Arial, sans-serif;
		margin: 20px auto;
	}

	div.dokuwiki div#sidebar {
		text-align: left;
		padding: 2px;
		font-size: 9px;
		background: none;
	}

	div.dokuwiki div#sidebar a {
		border: none !important;
	}

	/* POZADI */

	#dashboard #widget-area .widget-navbar .options-panel {
		background: none;
	}

	div.dokuwiki .bar,
	div.dokuwiki div.breadcrumbs, /* hlavicka */
	body {
		background-color: #181821 !important;
	}

	div#toc__inside, /* obsah */
	div.dokuwiki table.inline, /* tabulka */
	.dokuwiki .wrap_box { /* widgety */
		background-color: #131315 !important;
	}

	#dashboard #widget-area .widget-navbar .options-panel,
	html>body div.dokuwiki input.button,
	#dashboard .widget .header-content,
	div.dokuwiki div#sidebar,
	div.dokuwiki div#sidebar div.funding,
	div.dokuwiki div.tocheader,
	div.dokuwiki div.section_highlight,
	div.hiddenHead p, /* zahlavi - rozbalovaci texty - zadane testy atd. */
	div.dokuwiki div.comment_buttons input.button, /* tlacitka v diskuzi */
	button, /* tlacitka */
	div.dokuwiki input.edit, /* vyhledavaci policko */
	.notetip,
	.noteclassic,
	div.dokuwiki table.inline th { /* zahlavi tabulky */
		background-color: #1c1c1c !important;
	}

	img.math,
	img.mediacenter {
		padding: 3px;
		background-color: #eee;
	}

	/* BARVA TEXTU */

	body,
	strong,
	div.dokuwiki div#sidebar,
	div.dokuwiki div#sidebar div.funding p,
	html>body div.dokuwiki textarea.edit, /* pridavani komentare do diskuze */
	div.dokuwiki input.edit, /* vyhledavaci policko */
	div.dokuwiki table.inline { /* tabulka */
		color: #bbb !important;
	}

	#dashboard #widget-area .widget-navbar .options-panel,
	div.dokuwiki div.abutton,
	html>body div.dokuwiki input.button,
	#dashboard .widget .header-content,
	div.dokuwiki div.tocheader,
	div.dokuwiki .bar,
	div.dokuwiki div.breadcrumbs, /* hlavicka */
	div#toc__inside, /* obsah */
	button, /* tlacitka */
	div.hiddenHead p, /* zahlavi - rozbalovaci texty - zadane testy atd. */
	div.dokuwiki div.comment_buttons input.button, /* tlacitka v diskuzi */
	div.dokuwiki table.inline th, /* zahlavi tabulky */
	.dokuwiki .wrap_box { /* widgety */
		color: #ff99de;
	}

	div.dokuwiki p {
		color: #ccd;
	}

	/* OKRAJE */

	.dokuwiki div.wrap_round {
		border-radius: 0; /* zadne kulate okraje */
	}

	div.hiddenHead p, /* zahlavi - rozbalovaci texty - zadane testy atd. */
	div.hiddenGlobal { /* rozbalovaci texty - zadane testy atd. */
		border-color: #bbc;
	}

za	#dashboard #widget-area .widget-navbar .options-panel,
	div.dokuwiki div.abutton,
	html>body div.dokuwiki input.button,
	html>body div.dokuwiki textarea.edit, /* pridavani komentare do diskuze */
	div.dokuwiki input.edit, /* vyhledavaci policko */
	div.dokuwiki div.comment_buttons input.button, /* tlacitka v diskuzi */
	button { /* tlacitka */
		border-color: #ff99de;
	}

	#dashboard #widget-area .widget-navbar .options-panel,
	div.dokuwiki div.abutton,
	html>body div.dokuwiki input.button,
	html>body div.dokuwiki textarea.edit, /* pridavani komentare do diskuze */
	div.hiddenHead p, /* zahlavi - rozbalovaci texty - zadane testy atd. */
	div.hiddenGlobal, /* rozbalovaci texty - zadane testy atd. */
	div.dokuwiki input.edit, /* vyhledavaci policko */
	div.dokuwiki div.comment_buttons input.button, /* tlacitka v diskuzi */
	button { /* tlacitka */
		border-style: solid;
		border-width: 1px;
	}

	/* OSTATNI */

za	#dashboard #widget-area .widget-navbar .options-panel,
	div.dokuwiki div.abutton,
	html>body div.dokuwiki input.button,
	html>body div.dokuwiki textarea.edit, /* pridavani komentare do diskuze */
	div.dokuwiki div.comment_buttons input.button, /* tlacitka v diskuzi */
	button, /* tlacitka */
	div.dokuwiki input.edit { /* vyhledavaci policko */
		-webkit-appearance: none;
		background: none !important;
		padding: 2px;
	}

	/*---------------NADPISY---------------*/

	.dokuwiki h1, .dokuwiki h1 a {
		color: #f59 !important;
	}

	div.dokuwiki h2 a,
	.dokuwiki h2 {
		color: #f7a !important;
	}

	.dokuwiki h3 a,
	.dokuwiki h3 {
		color: #e6a !important;
	}

	.dokuwiki h4 a,
	.dokuwiki h4 {
		color: #8df !important;
	}

	/*----------ODRAZKOVANY TEXT-----------*/

	.li {
		color: #fbe !important;
	}

	/*---------------ODKAZY----------------*/

	div.t-centered.time-holder,
	div.dokuwiki div#sidebar a,
	div.dokuwiki a:link {
		color: #f9d !important;
	}

	div.dokuwiki div#sidebar a:hover,
	div.dokuwiki a:hover {
		color: #f59 !important;
	}

	#w_actual_courses_fit .content .show-hide-link,
	div.dokuwiki div#sidebar a:visited,
	div.dokuwiki a:visited {
		color: #f59 !important;
	}

	div.dokuwiki a.urlextern:link {
		color: #e7b !important;
	}

	/* odkaz na existujici stranku */
	div.dokuwiki a.wikilink1 {
		color: #fce !important;
		border-bottom: solid 1px;
		font-weight: bold;
		padding: 1px;
	}

	/* odkaz na NEexistujici stranku */
	div.dokuwiki a.wikilink2 {
		color: #f36 !important;
		border-bottom: dashed 1px !important;
	}

}
