css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp('https://progtest.fit.cvut.cz.*'){
body {
	background-color: #101010;
	font-size: 15px;
	margin: 20px auto;
	max-width: 1000px;
	text-align: center;
	color: #bbb;
}

body, input, button, select, tr {
	font-family: "Roboto", "Fira Sans", Arial, sans-serif !important;
}

tr.resRow td {
	font-size: 10px;
}

div.outBox table,
tr.resRow td,
.Cell, .lCell, .rCell, .tCell, .bCell, .ltCell, .rtCell, .lbCell, .rbCell, .ltbCell, .rtbCell, .lrbCell, .lrtCell, .tbCell, .lrCell, .lrtbCell,
tbody .lBox,
tbody .rBox,
tbody .mBox {
	background-color: #131315;
}

img {
		background-color:  #5df;
}

select,
tr.resHdr  td,
thead .hdrDis {
	background-color: #1c1c1c;
	color: #6ae;
	font-size: 15px;
	padding: 5px;
	text-align: center;
}

.tbSepCell,
.ltbSepCell,
.rtbSepCell,
.Cell, .lCell, .rCell, .tCell, .bCell, .ltCell, .rtCell, .lbCell, .rbCell, .ltbCell, .rtbCell, .lrbCell, .lrtCell, .tbCell, .lrCell, .lrtbCell,
tbody .lBox,
tbody .rBox,
tbody .mBox {
	border-style: none;
}

select,
thead .hdrDis,
tr.resHdr  td,
tr.resRow td,
.outBox {
	border: solid 1px #223;
}

div.but1 w120, .but2, a.butLink {
	background-color: #101010;
	color: #0af;
	border-color: #0af;
	-webkit-appearance: none;
	background: none !important;
	padding: 3px;
}

a {
	color: #9df;
}

a:hover {
	color: #d9f;
}

tr:hover {
	color: #0af;
}

a.butLink:hover {
	background-color: #0af !important;
	color: #101010;
	width: 95%;
}

tbody .navLink, a.navLink, span.navLink {
	color: #131315;
}

a.navLink:hover {
	color: #1c1c1c;
}

.lrtbOkSepCell, .ltbOkSepCell, .rtbOkSepCell, .tbOkSepCell,
table.header {
	color: #101010;
	border-style: none;
}
}
