css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document regexp("(.*)/(\\b(matrix|riot)\\.(.*))/(.*)") {
    .mx_SearchBox {
        padding-top: 0;
        padding-bottom: 0;
        margin-top: 0;
    }
    .mx_EventTile_editButton {
        -webkit-user-select: none;
        -moz-user-select: none;
        -ms-user-select: none;
        user-select: none;
    }
    .mx_EventTile_line, .mx_EventTile_content .markdown-body {
        font-size: 18px;
        line-height: 19px;
    }
    .mx_EventTile_content .markdown-body, .mx_EventTile_line {
        line-height: 22px;
    }
    .mx_EventTile_redacted .mx_EventTile_line .mx_UnknownBody {
		height: 20px;
    }
}
