css:
@-moz-document domain("youtubemultidownloader.net") {
:root {
    /* Main background color */
    --color-bg-main: #212121;
    /* Alternate background color */
    --color-bg-alt: #424242;
    /* Submission feedback status */
    --color-bg-good: #01579B;
    --color-bg-warn: #D32F2F;
    --color-bg-success: #388E3C;

    --color-bg-green: #9CCC65;
    /* Main text color */
    --color-text-main: #EEEEEE;
    /* Alternate text color */
    --color-text-alt: #9E9E9E;
    --color-text-warn: #EF9A9A;

    /* Link text color */
    --color-text-link: #81D4FA;
    --color-text-link-hover: #03A9F4;
    /* Typeface */
    --font-text-main: "Inconsolata","Consolas","Lucida Console",monospace !important;
}

body, .container {
    background-color: var(--color-bg-main);
}

.header {
    border-width: 0 !important;
    padding: 0 !important;
    margin-bottom: 16px !important;
}

.alert {
    border-radius: 0 !important;
    border-width: 0 !important;
    margin-bottom: 16px !important;
}

nav > ul > li > a {
    border-radius: 0 !important;
    border-width: 0 !important;
}

a {
    color: var(--color-text-link) !important;
}

h3, h4, h5, b {
    color: var(--color-text-main) !important;
}

td, th {
    border-top-width: 1px !important;
    border-bottom-width: 1px !important;
    border-color: var(--color-bg-alt) !important;
}

li[role=presentation] {
    margin-left: 2px !important;
    margin-right: 2px !important;
}

.panel,
.panel-default,
.active
 {
    border-radius: 0 !important;
    border-width: 0 !important;
    margin: 0 !important;
    background-color: var(--color-bg-alt) !important;
}

.active a {
    border-radius: 0 !important;
    border-width: 0 !important;
    margin: 0 !important;
    background-color: var(--color-bg-good) !important;
}

li > a:hover, li > a:active, li > a:focus {
    background-color: var(--color-bg-alt) !important;
}

.alert-success {
    background-color: var(--color-bg-alt);
}

.img-responsive,
#chkAutoOrder,
#chkAutoQuality {
    filter: invert(100%);
}

#cbQuality {
    background-color: var(--color-bg-alt) !important;
    color: var(--color-text-main) !important;
    border-color: var(--color-bg-alt) !important;
    margin-left: 4px !important;
}

#chkAutoOrder, #chkAutoQuality {
    margin-right: 4px !important;
}

#sizing-addon1,
#txtLink,
#btnDownloadVideo,
#txtPlaylist,
#txtDownload,
#Download,
#txtChannel,
.btn.btn-default,
.well.well-sm {
    background-color: var(--color-bg-main) !important;
    color: var(--color-text-alt) !important;
    border-radius: 0 !important;
    border-color: var(--color-bg-alt) !important;
}

.well.well-sm {
    border-top-width: 0 !important;
}

#___plusone_0,
#___plus_0,
#disqus_thread,
.footer,
.fb-share-button,
.fb_iframe_widget {
    display: none !important;
}

.progress {
    background-color: var(--color-bg-main) !important;
    border-radius: 0 !important;
}

.text-muted {
    font-size: 18px !important;
    font-family: "Inconsolata","Consolas","Lucida Console",monospace !important;
    font-weight: bold !important;
}

#lbError, .alert-danger {
    color: var(--color-text-main) !important;
    background-color: var(--color-bg-warn) !important;
}

.panel-body {
    color: var(--color-text-main) !important;
}

div.panel-body > span[style] {
    color: var(--color-text-warn) !important;
    font-weight: bold !important;
}

.panel-heading {
    font-family: "Inconsolata","Consolas","Lucida Console",monospace !important;
    background-color: var(--color-bg-main) !important;
    border-radius: 0 !important;
    border-width: 0 !important;
}

.label.label-default, a.btn.btn-default {
    background-color: var(--color-bg-warn) !important;
}

.label.label-success, .btn.btn-success {
    background-color: var(--color-bg-success) !important;
}

.btn.btn-primary,
.panel-body > .btn.btn-default,
.btn.btn-success,
.label.label-primary,
.label.label-default,
.label.label-success,
.label.label-info {
    border-radius: 0 !important;
    border-width: 0 !important;
    margin: 2px !important;
    color: var(--color-text-main) !important;
}

.thumbnail {
    border-radius: 0 !important;
    border-width: 0 !important;
    padding: 0 !important;
}
}
