css:
@-moz-document domain("v3rmillion.net") {
#logo img,
.logo {
    content: url(http://i.imgur.com/ph8L7tv.png) !important;
}
.pm_new,
.alert_new,
ul.menu li a.active {
    background: #10BA00 !important;
}
#bridge {
    border-bottom: 5px solid #0C9300;
}
ul.menu li ul,
.panel_buttons {
    border-top: 5px solid #0C9300;
}
.pagination .pagination_current {
    background: #0C9300;
    border: 1px solid #075800;
    border-radius: 2px;
}
.pagination a {
    background: #075800;
    border: 1px solid #042E00;
}
.pagination a:hover {
    transition: background 0.2s ease, border 0.2s ease;
    background: #0FB800;
    border: 1px solid #0088DD;
}
.thead {
    background: #0FAF01;
}
.popup_menu .popup_item:hover {
    color: #13DF00;
}
.buttons:hover {
    transition: border 0.2s ease;
    background: #13DF00;
    cursor: pointer;
}
.buttons {
    border: #13DF00;
}
a.postbit_qdelete,
a.postbit_website,
a.postbit_reply_pm,
a.postbit_forward_pm,
a.postbit_delete_pm,
a.postbit_email,
a.postbit_pm,
a.postbit_find,
a.postbit_edit,
a.postbit_quote,
a.postbit_multiquote,
a.postbit_multiquote_on,
a.postbit_report {
    background: #13DF00 !important;
    border: 1px solid #13DF00 !important;
}
a.button.new_thread_button,
button,
input.button,
a.button:link,
a.button:visited,
a.button:active {
    background: #1C8E00;
    border: 1px solid #11C600;
}
a.button.new_thread_button:hover,
button,
input.button:hover,
a.button:hover {
    transition: background 0.2s ease, border 0.2s ease;
    background: #23AF00;
    border: 1px solid #1C8E00;
}
.tfoot {
    background: #0B8100;
}
.usercp_nav_item:before {
    color: #0B8100;
}
.thead input.textbox,
.thead select {
    border: #0B8100;
}
ul.menu li a:hover,
.panel_buttons a:hover,
ul.menu li ul li a:hover {
    transition: background 0.5s ease;
    background: #0B8100;
}
.thread_head {
    border-bottom: 5px solid #0B8100 !important;
}
#footer {
    border-top: 5px solid #0B8100;
}
.forum_on {
    color: #0B8100;
}
.forum_off {
    color: #064A00;
}
.cRem a:visited,
.cRem a:link {
    color: #075900 !important;
}
.inline_row a:visited,
.inline_row a {
    color: #0C8C00 !important;
}
input.textbox:focus {
    transition: border 0.2s ease, box-shadow 0.2s ease;
    border: 1px solid #075900;
    box-shadow: 0 0 10px #032600;
}
form #message:focus {
    transition: border 0.2s ease, box-shadow 0.2s ease;
    border: 1px solid #0A7500;
    box-shadow: 0 0 10px #086500;
}
div#container span.smalltext a span strong {
    color: #086500 !important;
}
body {
    background: #094600 !important;
}
.trow1,
.trow2 {
    background: #1d2125;
    border-bottom: 1px solid #092E00;
}
#content {
    background: #051A00;
    border: 1px solid #0B5200;
    width: auto !important;
    padding: 20px;
    padding-bottom: 0px !important;
    margin-bottom: 40px;
    overflow: hidden;
}
}
