css:
@-moz-document domain("wikipedia.org") {
@font-face {
    font-family: 'Source Code Pro';
    font-style: normal;
    font-weight: 400;
    src: local('Source Code Pro'), local('SourceCodePro-Regular'), url(https://themes.googleusercontent.com/static/fonts/sourcecodepro/v4/mrl8jkM18OlOQN8JLgasDxBHWFfxJXS04xYOz0jw624.woff) format('woff');
}

body {
    background: #f5efdd !important;
/*     background: #fff !important; */
    font-size: 16px !important;
}

body, input, div, p, span {
    font-family: 'Segoe UI' !important;
}

b {
    font-weight: bold !important;
}

i {
    font-style: italic !important;
    font-family: 'Segoe UI' !important;
    font-weight: 400 !important;
}

.mw-headline {
    font-family: 'Cambria' !important;
}

#content > h1 {
    border: 0px !important;
    font-family: 'Cambria' !important;
    font-weight: 700 !important;
    font-size: 3em !important;
}

#content h2 {
    border: 0px !important;
    font-family: 'Cambria' !important;
    font-weight: 700 !important;
    font-size: 26px !important;
}

#content h3 {
    border: 0px !important;
    font-family: 'Segoe UI' !important;
    font-weight: 700 !important;
    font-size: 16px !important;
}

table {
    box-shadow: none !important;
    background: transparent !important;
}

#content h3 {
    font-size: 20px !important;
}

p {
    font-weight: 400 !important;
    line-height: 1.65 !important;
    margin-bottom: 16px !important;
}

table, td, th {
    border: 0px !important;
    border-collapse: collapse !important;
    padding: 5px !important;
}

ul, li {
    background: transparent !important;
    border: 0px !important;
    line-height: 1.8 !important;
    font-size: 14px !important;
    font-weight: 400 !important;
    list-style-image: none !important;
}

li span {
    background: transparent !important;
    border: 0px !important;
    font-size: 14px !important;
}

li {
    margin-bottom: .2em !important;
}

.reference, hr {
    display: none;
}

dl {
    font-weight: 400 !important;
}

dt {
    font-style: italic;
    font-size: 16px !important;
    margin-top: 10px !important;
}

dd {
    font-size: 15px !important;
    margin-bottom: .8em !important;
}

#p-logo a {
    background-image: url('http://icons.iconarchive.com/icons/sykonist/popular-sites/128/Wikipedia-icon.png') !important;
    background-size: 70px 70px !important;
    background-position: 35%;
    opacity: 0.1;
    height: 70px !important;
}

#p-logo {
    top: -66px !important;
    height: 70px !important;
}

#p-logo a:hover {
    opacity: 0.8;
}

code {
    padding: 3px !important;
    background: transparent !important;
    color: #39892f !important;
}

tt {
    font-family: 'Source Code Pro', 'Lucida Sans Unicode', 'Courier New' !important;
    font-size: 16px !important;
}

a {
    text-decoration: none;
}

.internal {
    display: none !important;
}

a[class^="external"], a[href$=".pdf"].external, a[href*=".pdf?"].external, a[href*=".pdf#"].external, a[href$=".PDF"].external, a[href*=".PDF?"].external, a[href*=".PDF#"].external, span.PDFlink a {
    background-image: none !important;
    text-decoration: none;
    padding-right: 0px !important;
    /*border-bottom-color: rgba(255,87,34,0.25) !important;*/
    border-bottom-color: rgba(205, 220, 57, 0.46) !important;
}

.bkl-link {
    background-color: rgba(255, 218, 218, 0.28);
    padding-left: 4px;
    padding-right: 4px;
    padding-top: 1px;
    padding-bottom: 2px;
}

#bodyContent a:link {
    color: #065580 !important;
    /*color: #064161 !important;*/
    border-bottom-width: 1px;
    border-bottom-color: rgba(6, 85, 128, .1);
    /*border-bottom-color: rgba(0,0,0,0.10);*/
    padding-bottom: 1px;
    text-decoration: none;
    border-bottom-style: solid;
}

#bodyContent a:hover {
    color: #d04000 !important;
    border-bottom-color: rgba(208, 64, 0, 0.10) !important;
}

#bodyContent a:visited {
    color: #065580 !important;
    border-bottom-color: rgba(6, 85, 128, .1) !important;
}

#bodyContent a:visited:hover {
    color: #d04000 !important;
    border-bottom-color: rgba(208, 64, 0, 0.10) !important;
}

/*#bodyContent a:visited {
        color: green !important;
        border-bottom-color: green !important;
    }*/
a.image {
    border-bottom: none !important;
    color: #000000 !important;
    text-decoration: none;
}

img a:hover {
    border-width: 1px;
    border-color: #000000 !important;
    text-decoration: none;
}

a.image:visited {
    color: #000000 !important;
    text-decoration: none;
}

.new, .new:link, .new:visited {
    color: #cd5b45 !important;
}

blockquote {
    border: 0px !important;
    font-style: italic !important;
    font-size: 15px !important;
    margin-left: 0px !important;
    margin-right: 0px !important;
    margin-top: 15px !important;
    margin-bottom: 15px !important;
    background: transparent !important;
    color: /*#607d8b*/
    #000 !important;
    border-left: 2px solid #CFD8DC !important;
}

blockquote > :last-child:last-of-type {
    font-size: 8px;
    margin: 0 !important;
    text-align: left !important;
}

blockquote > :only-child:only-of-type {
    font-size: 15px;
    margin: 0 !important;
    text-align: left !important;
}

select {
    padding: 0px 10px;
}

.mw-ui-button, .mw-ui-progressive {
    background: #347bff !important;
    color: #fff !important;
    text-shadow: none !important;
    box-shadow: none !important;
    border: none !important;
    padding: 9px 16px !important;
    margin: 5px 10px 5px 0px !important;
    font-size: 0.875em !important;
}

.mw-ui-button, .mw-ui-progressive, .mw-ui-button:link, .mw-ui-progressive:link, .mw-ui-button:visited, .mw-ui-progressive:visited {
    color: #fff !important;
}

.mw-ui-button:hover, mw-ui-progressive:hover {
    background: rgba(52, 123, 255, 0.85) !important;
}

.mw-ui-button:active, mw-ui-progressive:active {
    box-shadow: 0 0 15px 0 rgba(0, 0, 0, 0.3) inset !important;
}

#wpSave, #wpPreview, #wpDiff {
    border-radius: 4px !important;
    border: 1px solid #347Bff !important;
    color: #347Bff !important;
    background: #fff !important;
    text-shadow: 0px 0px 0px;
    padding: 0.5em 1em;
    cursor: pointer;
}

#wpSave:hover, #wpPreview:hover, #wpDiff:hover {
    color: rgba(52, 123, 255, 0.5) !important;
    ;
}

/* Search */
#mw-head #searchform > div {
    position: absolute !important;
    top: 34px !important;
    right: 36px !important;
    margin-top: 13px !important;
    margin-left: 15px !important;
    border: none !important;
    background: #f5efdd !important;
    width: 200px !important;
}

#simpleSearch {
    border: 0 !important;
    margin: 0 !important;
    background: #f5efdd !important;
    padding-right: 0 !important;
}

#searchInput, .mw-ui-input, .oo-ui-inputWidget-input {
    border-bottom: 2px solid #347bff !important;
    border-top: none !important;
    border-left: none !important;
    border-right: none !important;
    background: transparent !important;
    color: #000 !important;
    padding: 7px !important;
    font-size: 1em !important;
}

.mw-ui-input:focus {
    box-shadow: none !important;
}

.oo-ui-inputWidget-input:focus {
    border-bottom: 2px solid #347bff !important;
    border-top: none !important;
    border-left: none !important;
    border-right: none !important;
}

#search {
    border: 0px !important;
    background: transparent !important;
    font-size: 16px !important;
    padding: 4px 7px 7px 7px !important;
}

#searchButton {
    display: none;
    right: 18px !important;
    top: 5px !important;
    background-image: none;
}

/* Search results in box */
.suggestions {
    font-size: 0.875em !important;
    max-width: 200px !important;
    margin-top: 15px !important;
    border: 0px !important;
    box-shadow: 0 10px 10px 0 rgba(0, 0, 0, 0.3);
}

.suggestions-results {
    border: 0px !important;
}

.suggestions-result-current {
    background: rgb(251, 244, 184) !important;
    color: #000 !important;
}

.suggestions-result, .suggestions-special {
    padding: 10px !important;
}

.suggestions-special {
    border: none !important;
}

.suggestions-special div {
    color: #000 !important;
    font-style: italic !important;
}

.imeselector, #searchEngines {
    display: none !important;
}

.mw-search-formheader {
    border: 0px !important;
    background: transparent !important;
}

.mw-search-result-heading, .searchresult, .searchmatch {
    font-size: 14px !important;
    font-weight: 400 !important;
    padding: 3px 0px !important;
}

.searchmatch {
    font-weight: 700 !important;
}

/* Search results on page */
.mw-search-result-data {
    color: #808080 !important;
    font-size: 0.875em !important;
}

/* Page */
#mw-page-base, .vectorTabs {
    background: transparent !important;
    border: 0px !important;
}

#content {
    margin: 0 auto !important;
    border: 0px !important;
    /*margin-left: 215px !important;*/
    background: #f5efdd !important;
    max-width: 770px !important;
    zoom: 125% !important;
}

.portal {
    background: transparent !important;
}

div#mw-panel div.portal div.body {
    margin: 0 0 0.2em 0.2em !important;
}

div#p-navigation div.body {
    margin: 0 0 0.2em 0.2em !important;
}

#mw-panel {
    width: 155px !important;
    top: 100px !important;
    box-shadow: 5px 0px 0px 0 rgba(0, 0, 0, 0.1);
}

#mw-panel ul > li {
    /*        line-height: 1.3em !important;*/
    font-size: 11px !important;
}

#mw-panel ul > li > a {
    color: #4c4c4c !important;
    opacity: 0.3 !important;
}

#mw-panel ul > li > a:hover {
    color: #d04000 !important;
    border-bottom-color: rgba(208, 64, 0, 0.10) !important;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    padding-bottom: 1px;
    text-decoration: none;
    opacity: 0.8 !important;
}

#mw-panel ul > li > a:visited:hover {
    color: #d04000 !important;
    border-bottom-color: rgba(208, 64, 0, 0.10) !important;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    padding-bottom: 1px;
    text-decoration: none;
    opacity: 0.8 !important;
}

#mw-head {
    opacity: 1.0;
    -webkit-transition-property: opacity !important;
    -moz-transition-property: opacity !important;
    -o-transition-property: opacity !important;
    -ms-transition-property: opacity !important;
    transition-property: opacity !important;
    -webkit-transition-duration: 0.5s !important;
    -moz-transition-duration: 0.5s !important;
    -o-transition-duration: 0.5s !important;
    -ms-transition-duration: 0.5s !important;
    transition-duration: 0.25s !important;
}

#mw-head:hover {
    opacity: 1.0 !important;
}

.uls-settings-trigger {
    display: none !important;
}

#footer {
    padding-left: 3em !important;
}

#p-lang-label, #p-wikibase-otherprojects-label, #p-coll-print_export-label, #p-tb-label, #p-participation-label, #p-navigation-label {
    font-size: 13px !important;
    opacity: 0.65 !important;
}

.wb-otherproject-commons:before, .wb-otherproject-wikidata:before {
    display: none !important;
}

.mw-interlanguage-selector {
    opacity: 0.2 !important;
}

.mw-interlanguage-selector:hover {
    opacity: 0.8 !important;
}

/* Заглавная, обсуждение */
#left-navigation {
    margin-left: 233px !important;
}

/* Standard table */
.wikitable {
    font-weight: 400 !important;
}

.wikitable td, .wikitable th {
    border: 1px solid rgba(0, 0, 0, 0.07) !important;
}

/* Code */
pre {
    line-height: 1.3 !important;
    font-family: 'Source Code Pro', 'Lucida Sans Unicode', 'Couriew New', 'Arial' !important;
    border: 0px !important;
    background: 0px !important;
    font-size: 16px !important;
}

.mw-code {
    border: 0px !important;
    padding: 17px !important;
    background: 0px !important;
    font-size: 16px !important;
}

/* Index */
.toc {
    background: #f5efdd !important;
    max-width: 300px !important;
    border-left: 0px solid #347bff !important;
    border-top: none !important;
    border-right: none !important;
    border-bottom: none !important;
    opacity: 0.9 !important;
    font-size: 0.875em !important;
    display: none !important;
}

.toc div, .toc span {
    font-size: 0.875em !important;
    display: none !important;
}

.toc, #toc, .toccolours {
    padding: 0 !important;
    margin-top: 25px !important;
    display: none !important;
}

#toc #toctitle, .toc #toctitle, #toc .toctitle, .toc .toctitle {
    text-align: left !important;
    display: none !important;
}

.toctoggle {
    display: none !important;
}

/* Block with image */
.thumbinner, .thumb, .toccolours, .graytable {
    background: transparent !important;
    border: 0px !important;
    font-size: 16px !important;
}

.thumbimage {
    border: 0px !important;
}

.thumbcaption {
    font-style: italic !important;
    margin: 7px 0px !important;
}

/* Курсивный небольшой жирноватый шрифт в начале страницы */
.dablink {
    margin: 14px 0px !important;
    padding: 0 !important;
    font-size: 18px !important;
    color: #4c4c4c !important;
}

#contentSub {
    margin: 15px 0 !important;
}

.rellink {
    padding: 0 !important;
}

.hatnote {
    padding: 0 !important;
}

/* Надпись - материал из вики и текущая страница не проверялась */
#siteSub, .flaggedrevs_preview {
    display: none !important;
}

/* Tables under a page */
.ambox, .tmbox, .Note, .informationbox {
    margin: 2px 0px !important;
    font-size: 10px !important;
    font-weight: 400 !important;
    font-style: italic !important;
    line-height: 1.5 !important;
    border: 1px dashed rgba(170, 170, 170, 0.5) !important;
    opacity: .1 !important;
    /* max-width: 550px !important; */
    ;
}

.ambox td, .tmbox td {
    padding: 10px !important;
}

.ambox-text-small, .mbox-text, .Note td {
    font-size: 10px !important;
    color: #000 !important;
}

.ambox-image, .mbox-image {
    display: none;
}

#request_for_deletion, .ambox-serious {
    background: rgba(255, 0, 0, 0.1) !important;
    border: 1px dashed rgba(170, 170, 170, 0.38) !important;
    color: #a00000 !important;
}

.tmbox-notice, .tmbox-move, .Note {
    background: rgba(150, 75, 0, 0.1) !important;
    border: 1px dashed rgba(150, 75, 0, 0.15) !important;
}

.informationbox {
    background: #fff !important;
    padding: 10px !important;
    font-size: 14px !important;
}

.editOptions {
    background: #fff !important;
    border: 0px !important;
}

/* Ссылки на категории */
.catlinks {
    background: transparent !important;
    border: 1px dashed #ccc !important;
}

/* Tables on the right */
.infobox, .tright, .toccolours {
    background: transparent !important;
    border-collapse: collapse !important;
    border: 1px solid rgba(0, 0, 0, 0.04) !important;
    box-shadow: none !important;
    zoom: 70% !important;
    font-size: 60% !important;
/*     font-size: 47% !important; */
}

.infobox td, .infobox th, .tright td, .tright th, .toccolours td, .toccolours th {
    padding: 10px !important;
    background: transparent !important;
    border: 0 !important;
}

tr {
    background: transparent !important;
}

/* Главный блок на заглавной странице */
.globegris {
    background: transparent !important;
}

/* Мини-символы у ссылки править и примечаний */
.mw-editsection-bracket, .references-small b, .mw-cite-backlink, .plainlinksneverexpand {
    display: none !important;
}

#floating_object {
    display: none;
}

/* Нижние ссылки по навигации и навигация по разделам навигации*/
.navbox, .nowraplinks {
    margin: 10px 0px !important;
    border-collapse: collapse !important;
    border: none !important;
    background: transparent !important;
}

.navbox td, .navbox th, .nowraplinks td, .nowraplinks th {
    padding: 9px 6px !important;
    font-size: 14px !important;
    border: none !important;
    background: transparent !important;
}

.navbox-title img {
    display: none !important;
}


/*     .navbox-title {
				background: #fff !important ;
    } */
.navbox div {
    border: none !important;
    background: transparent !important;
}

.NavFrame {
    border: 0px !important;
    background: transparent !important;
    padding: 0px !important;
}

.NavHead {
    margin: 10px 0px !important;
    padding: 12px !important;
    font-size: 13px !important;
    font-weight: 400 !important;
    background: transparent !important;
}

.NavContent {
    font-size: 11px !important;
    background: transparent !important;
}

.NavContent > ul li {
    font-size: 11px !important;
    background: transparent !important;
}

.Boxmerge {
    border: 0px !important;
}

/* Coordinates */
#coordinates {
    display: none !important;
}

#mw-fr-revisiontag, #mw-fr-reviewnotice {
    display: none !important;
}

/* New boxes */
.oo-ui-buttonElement-framed.oo-ui-widget-enabled.oo-ui-buttonElement-active > .oo-ui-buttonElement-button {
    color: #fff !important;
    background: #347bff !important;
}

.oo-ui-labelElement-label {
    text-shadow: none !important;
}

.oo-ui-optionWidget-pressed {
    background: #fff !important;
}

.oo-ui-optionWidget-selected {
    background: rgba(0, 0, 0, 0.1) !important;
}

/* Tooltip */
.referencetooltip {
    background: #fff !important;
    box-shadow: 2px 2px 10px 2px rgba(0, 0, 0, 0.5) !important;
}

/* Popup */
#mwe-popups-settings {
    box-shadow: none !important;
}

/**** EXSABUTA ****/

/* Yello pop-ups (START) */

/*   .popupPreview {
	font-size:18px;
} */
.popupTopLinks {
    font-size: 3px;
    display: none !important;
}

.popupData {
    font-size: 3px;
    display: none !important;
}

.navpopup {
    background-color: rgb(251, 244, 184) !important;
    padding: 40px !important;
    font-size: 18px !important;
    border: 0 !important;
    max-width: 480px !important;
}

/* Yello pop-ups (END) */

/* Edit sections (START) */
.mw-editsection a {
    font-family: "Segoe UI", Helvetica, Arial, serif !important;
    color: #333 !important;
    font-size: 12px !important;
    margin-right: 10px !important;
    vertical-align: 2% !important;
    opacity: 0 !important;
}

.mw-editsection a:hover {
    opacity: 1 !important;
    -webkit-transition: all .25s ease-in-out .5s;
    -moz-transition: all .25s ease-in-out .5s;
    -o-transition: all .25s ease-in-out .5s;
    -ms-transition: all .25s ease-in-out .5s;
    transition: all .25s ease-in-out .5s;
}

.mw-editsection {
    vertical-align: 30% !important;
}

.mw-editsection-bracket {
    font-family: "Segoe UI", Helvetica, Arial, serif !important;
    display: none !important;
    font-size: 12px !important;
}

.mw-editsection-divider {
    font-family: "Segoe UI", Helvetica, Arial, serif !important;
    display: none !important;
    font-size: 12px !important;
}

/* Edit sections (END) */

/* User menu (START) */
#pt-notifications-alert {
    background-color: #f5efdd !important;
    font-weight: normal !important;
    padding: 0px !important;
    display: none !important;
}

#pt-notifications-notice {
    background-color: #f5efdd !important;
    font-weight: normal !important;
    padding: 0px !important;
    display: none !important;
}

#pt-notifications-message {
    background-color: #f5efdd !important;
    font-weight: normal !important;
    padding: 0px !important;
    display: none !important;
}

#p-personal {
    right: 2.3em !important;
    opacity: 0 !important;
}

#p-personal:hover {
    opacity: 1 !important;
    -webkit-transition: all .25s ease-in-out .5s;
    -moz-transition: all .25s ease-in-out .5s;
    -o-transition: all .25s ease-in-out .5s;
    -ms-transition: all .25s ease-in-out .5s;
    transition: all .25s ease-in-out .5s;
}

#p-personal a:link {
    opacity: 0.25 !important;
    color: #000000 !important;
    border-bottom-width: 1px;
    border-bottom-color: rgba(0, 0, 0, 0.10);
    border-bottom-style: solid;
    padding-bottom: 1px;
    text-decoration: none;
}

#p-personal a:hover {
    color: #d04000 !important;
    border-bottom-color: rgba(208, 64, 0, 0.10) !important;
    opacity: 0.7 !important;
}

#p-personal a:visited {
    opacity: 0.5 !important;
    color: green !important;
    border-bottom-color: green !important;
}

#p-personal a:visited:hover {
    opacity: 1 !important;
    color: #d04000 !important;
    border-bottom-color: rgba(208, 64, 0, 0.10) !important;
}

#mw-head-base {
    height: 1em !important;
}

.cx-callout {
    display: none !important;
}

/* User menu (END) */

/* Global (START) */
#content {
    border-color: #eee !important;
    background-color: #f5efdd !important;
}

a {
    color: #3574AB !important;
}

#mw-panel {
    box-shadow: none !important;
}

#footer {
    display: none !important;
}

.catlinks {
    margin-top: 2em !important;
    display: none !important;
}

.after-portlet {
    display: none !important;
}

.after-portlet-lang {
    display: none !important;
}

table {
    background: #f5efdd;
}

.mw-body-content {
    font-size: 1.3em !important;
}

/* Global (END) */

/* Article (START) */
.firstHeading {
    max-width: 730px !important;
    line-height: 1 !important;
}

.dablink {
