css:
@-moz-document domain("www.ziaruldeiasi.ro") {
body {
    background: #f3f3f3;
    margin: 0;
    font-family: 'Segoe UI', sans-serif;
    color: #444;
    font-size: 1rem;
    line-height: normal;
    position: inherit;
    /*min-width: 78rem;*/
}
ul,
ol {
    list-style-type: none;
    margin: 0;
    padding: 0;
}
a:hover {
    color: inherit;
}
.clear {
    clear: none;
    display: none;
}
body > img,
.top-banner,
.cc_banner-wrapper,
.xclear,
#adogadp1{
    display: none;
}
.top,
.menu,
.mid {
    max-width: 78rem;
	margin: 0 auto;
}
/***************************

	TOP

***************************/

.top > .center {
    background: none;
    background-color: #b22222;
    /*#b22222;*/
    /*#258c8c*/
    margin: 0;
    width: auto;
    box-shadow: none;
}
.top .head-menu {
    display: flex;
    flex-flow: row nowrap;
    justify-content: space-between;
    align-items: center;
    padding: .5rem;
    background-color: rgba(0, 0, 0, .05);
    /* border-bottom: 1px solid rgba(255, 255, 255, .1); */
    width: auto;
    height: auto;
}
.top .head-menu > div > ul {
    display: flex;
    flex-flow: row nowrap;
    align-items: center;
}
.top .head-menu > div > ul li {
    color: rgba(255, 255, 255, .5);
    line-height: normal;
    font-size: inherit;
    display: inline-block;
}
.top .head-menu > div > ul li a {
    display: block;
    padding: .25rem .5rem;
    font-size: .9rem;
    color: #fff;
    text-decoration: none;
    white-space: nowrap;
    /*font-family: 'Roboto Condensed', sans-serif;*/
}
.top .head-menu > div > ul li a:hover {
    text-decoration: underline;
}
.top .head-menu-left {
    float: none;
}
.top .head-menu-cont {
    float: none;
    padding: 0;
}
.top .head-menu-right {
    float: none;
    padding: 0;
    display: flex;
    flex-flow: row nowrap;
    justify-content: space-between;
    align-items: center;
    flex: 1 1 100%;
}
.top .head {
    padding: 0;
}
.top .head > div {
    padding: 0 !important;
}
.head > div {
    display: flex;
    flex-flow: row nowrap;
    justify-content: space-between;
    align-items: center;
}
.head-left {
    width: auto;
    float: none;
    /* padding: 2rem 1.5rem; */
    
    padding: 1rem 1.5rem 1.5rem;
}
.head-left .logo {
    background: none;
    width: auto;
    height: auto;
    visibility: hidden;
    float: none;
    display: inline-block;
    /*font-family: 'Oswald', sans-serif;*/
    font-family: Impact, sans-serif;
    color: #fff;
    text-decoration: none;
    font-weight: 100;
    font-size: 0;
    text-indent: 0;
    position: relative;
    margin-bottom: 1rem;
    text-align: center;
}
.head-left .logo:before {
    display: block;
    content: "Ziarul de Iași";
    visibility: visible;
    font-size: 3rem;
}
.head-left .logo:after {
    display: block;
    content: "Liderul presei ieșene";
    visibility: visible;
    /*font-family: 'Roboto Condensed', sans-serif;*/
	font-family: 'Calibri', sans-serif;
    font-size: .9rem;
    font-weight: bold;
    letter-spacing: .1rem;
    text-transform: uppercase;
    text-align: center;
    position: absolute;
    bottom: -.5rem;
    left: 0;
    right: 0;
}
.head-left > p {
    margin: 0;
    padding: 0;
    font-size: .75rem;
    text-align: center;
    text-transform: uppercase;
    font-weight: 300;
    color: rgba(255, 255, 255, .9);
    text-shadow: none;
}
.head-right {
    float: none;
    margin: 0;
    width: auto;
    height: auto;
    display: none;
}
.top .search {
    /*margin-left: 1rem;*/
    width: auto;
    height: auto;
    position: static;
    right: auto;
    top: auto;
}
.top input.submit-search {
    background-position: top right;
    position: static;
    top: auto;
    right: auto;
    border: 0;
    height: 2rem;
    background-color: #eee;
    border-radius: 0 1px 1px 0;
    width: 2.5rem;
    cursor: pointer;
}
.top input.input-search {
    background: none;
    width: auto;
    position: static;
    left: auto;
    top: auto;
    font-size: inherit;
    color: inherit;
    border: 0;
    font-family: inherit;
    font-weight: 300;
    padding: .5rem .75rem;
    background-color: #fff;
    border-radius: 1px 0 0 1px;
    height: 2rem;
    box-sizing: border-box;
}
/***************************

	MENU

***************************/

.menu .menu-wrap {
    background-color: #b22222;
    /*#b22222;*/
    /*#258c8c*/
    /*#409494*/
    /*box-shadow: 0 -1px 0 0 rgba(255, 255, 255, .05);*/
	box-shadow: none;
    width: auto;
    height: auto;
}
.menu .menu-wrap > ul {
    display: flex;
    flex-flow: row wrap;
    padding: 0;
    width: auto;
    height: auto;
    box-shadow: none;
    border: 0;
    background: none;
    background-color: transparent; /* rgba(0, 0, 0, 0.05); */
}
.menu .menu-wrap > ul li {
    float: none;
    display: inherit;
}
.menu .menu-wrap > ul li a {
    display: block;
    background: none;
    height: auto;
    line-height: normal;
    /* padding: .5rem; */
    
    padding: .5rem 1rem;
    color: #fff;
    font-size: 1.25rem;
    font-weight: bold;
    text-decoration: none;
    text-transform: none;
    text-shadow: none;
    white-space: nowrap;
    /*font-family: 'Oswald', sans-serif;*/
    font-family: 'Calibri', sans-serif;
    transition: all 0s ease 0s;
    border: 0;
}
.menu .menu-wrap > ul li a:hover{
    background-color: rgba(0, 0, 0, .1);
}
.menu .menu-wrap > ul li a.selected{
    background-color: #444;
}
.menu .menu-wrap ul li:last-child a{
    width: auto;
}
.menu .custom-menu {
    background-color: #333;
    width: auto;
    clear: none;
    border: 0;
    margin: 0;
	border-bottom: .5rem solid #b22222;
}
.menu .custom-menu > ul {
    display: flex;
    flex-flow: row wrap;
    padding: .5rem;
}
.menu .custom-menu > ul li {
    display: inline;
}
.menu .custom-menu > ul li a {
    display: block;
    background: none;
    border-radius: 0;
    box-shadow: none;
    margin: 0;
    padding: .25rem .5rem;
    font-size: .9rem;
    line-height: normal;
    /* color: #333; */
    
    color: #ddd;
    text-decoration: none;
    white-space: nowrap;
    /*font-family: 'Roboto Condensed', sans-serif;*/
    font-weight: normal;
}
.menu .custom-menu > ul li a:hover {
    color: #fff;
    text-decoration: underline;
}
.menu .custom-menu .selected {
    color: #b22222 !important;
    /*#b22222;*/
    /*#258c8c*/
}
/***************************

	MID

***************************/

.mid {
    box-shadow: 0 0 0 1px rgba(0, 0, 0, .1);
}
.mid > .center {
    background: none;
    background-color: #fff;
    display: flex;
    flex-flow: row wrap;
    justify-content: space-between;
    margin: 0;
    width: auto;
    box-shadow: none;
}
.mid .sub-meniu-banner {
    _flex: 1 0 100%;
    display: none;
}
.mid .wrap-left {
    width: 75%;
    box-sizing: border-box;
    float: none;
}
.mid .wrap-right {
    width: 25%;
    float: none;
    margin: 0;
    padding: 0 0 2rem;
}
.wrapper-slideshow {
    display: block;
    height: auto !important;
    width: auto;
    overflow: visible;
}
#main_navi {
    display: none;
    float: none;
}
#main_navi li {
    clear: none;
    color: inherit;
    cursor: inherit;
    font-size: inherit;
    height: auto;
    width: auto;
}
#pages {
    display: block;
    position: static !important;
    float: none;
    margin: 0;
    width: auto !important;
    height: auto !important;
    ;
    overflow: visible !important;
}
#pages .page {
    display: block !important;
    position: static !important;
    top: auto !important;
    left: auto !important;
    opacity: 1 !important;
    z-index: auto !important;
    width: auto !important;
    height: auto !important;
}
.yos-acasa-box1 .thumb-float-right,
.yos-acasa-box2 .thumb-float-right {
    float: left !important;
    width: auto;
    height: auto;
    overflow: visible;
    display: block;
    margin: 0;
}
/***************************

	ARTICLE

***************************/

.article {
    float: none;
    width: 100%;
    margin: 0;
    box-sizing: border-box;
    height: 100% !important;
    padding: 1rem;
    color: #333;
    overflow: visible;
    box-shadow: -1px 1px 0 0 rgba(0, 0, 0, .1) inset;
}
.article.fixed.colStiri {
    height: auto !important;
}
.article.featured {
    background-color: inherit;
    padding: 1rem !important;
}
.section-featured {
    padding: 1rem;
    margin: 0;
    background: none;
    box-shadow: -1px 1px 0 0 rgba(0, 0, 0, .1) inset;
}
.section-featured-thumb,
.article .art-thumb {
    display: block;
    float: left;
    width: auto;
    height: auto;
    margin: 0 1rem 1rem 0;
}
.section-featured-thumb img{
    max-width: 100%;
}
.article .art-thumb img {
    border: 1px solid rgba(0, 0, 0, .1);
}
.article.featured .art-thumb {
    display: inline-block;
    margin: 0 1rem 1rem 0 !important;
}
.article.featured .art-thumb > img {
    margin: 0;
    /* width: 19.2rem; */
    
    width: 300px;
    height: auto;
    /*16rem;*/
    
    border-radius: 1px;
}
.article.fixed .art-thumb {
    float: left !important;
}
.article.fixed .art-thumb > img {
    float: none;
    margin: 0;
    /* width: 10rem; */
    
    width: 150px;
    height: auto;
    /* 8rem;*/
    
    border-radius: 1px;
}
.thumb-float-left {
    float: none !important;
}
.article a {
    /*font-family: 'Roboto Condensed', sans-serif;*/
    color: #333;
}
.section-featured-title,
.article .art-title {
    /*font-family: 'Roboto Slab', serif;*/
	font-family: inherit;
    margin: 0 0 1rem;
    height: auto !important;
    font-size: 1.5rem;
	font-weight: 700;
    overflow: visible;
    line-height: 120%;
	margin-bottom: 1rem;
}
.article.featured .art-title {
    font-size: 1.75rem;
	height: auto;
}
.section-featured-title a,
.article .art-title > a {
    /* font-family: 'Roboto Slab', serif; */
	font-family: inherit;
    text-decoration: none;
    color: inherit;
    transition: all 0s ease 0s;
}
.section-featured-title a:hover,
.article .art-title > a:hover {
    text-decoration: underline;
    color: inherit;
}
.article.featured .art-title > a {
    font-size: inherit;
    padding: 0;
    display: inline;
}
.section-featured-desc,
.article .art-description,
.article.featured .art-description {
    padding: 0;
    height: auto;
    font-size: inherit;
    font-weight: normal;
    line-height: normal;
    margin: 0 0 1rem !important;
    text-indent: inherit;
    text-indent: 0;
    overflow: visible;
}
.section-featured-desc a,
.article .art-description a,
.article .art-description .txt-link {
    text-decoration: none;
    color: inherit;
    font-size: inherit !important;
    font-weight: 400;
    transition: all 0s ease 0s;
}
.section-featured-desc a,
.article .art-description a:hover {
    color: inherit !important;
}
.article.featured .art-description a {
    line-height: normal;
}
.article .art-description .arr {
    display: none;
}
.section-featured .art-citeste-tot,
.article .art-citeste-tot {
    position: inherit;
    bottom: auto;
    right: auto;
    z-index: auto;
    line-height: normal;
    height: auto;
    font-size: inherit;
    font-weight: inherit;
    background: none;
    display: inline-block;
    display: none;
    background-color: #444;
    color: #fff;
    padding: .5rem 1rem;
    text-decoration: none;
}
.article .art-citeste-tot:hover {
    background-color: #555;
}
.section-featured .art-wrap,
.article .art-wrap {
    background-color: inherit;
    bottom: auto;
    left: auto;
    height: auto;
    position: static;
    width: auto;
    clear: both;
    display: flex;
    justify-content: flex-end;
    align-items: center;
}
.article.featured .art-wrap {
    background-color: inherit !important;
}
.section-featured .art-wrap .art-com,
.article .art-wrap .art-com {
    left: auto;
    top: auto;
    position: static;
    line-height: normal;
}
.section-featured .art-wrap .art-com a,
.article .art-wrap .art-com a {
    display: inline;
    color: inherit;
    text-decoration: none;
    font-size: inherit;
    font-weight: inherit;
    line-height: normal;
    padding: 0;
}
.section-featured .art-wrap .art-com a:hover,
.article .art-wrap .art-com a:hover {
    text-decoration: underline;
    line-height: normal;
}
.article .art-wrap .art-cat {
    display: flex;
    position: static;
    right: auto;
    top: auto;
    flex-flow: row nowrap;
    justify-content: flex-end;
    align-items: center;
    /*border-left: 1px solid #ddd;*/
    
    margin-left: 1rem;
    /*padding-left: 1rem;*/
}
.article .art-wrap .art-cat a {
    display: inline;
    color: #999;
    height: auto !important;
    line-height: normal !important;
    font-size: inherit;
    font-weight: inherit;
    padding: 0;
    text-transform: uppercase;
}
.article .art-wrap .art-cat .cat-def {
    display: inline-block;
    /* background-color: #444; */
    
    color: #fff;
    padding: .25rem .5rem;
    text-decoration: none;
}
.article .art-wrap .art-cat .toate_stirile {
    display: none;
}
.section-featured .art-tags,
.article .art-tags {
    background-color: inherit;
    bottom: auto;
    min-height: 0;
    padding: 0;
    position: static;
    width: auto;
    font-weight: 400;
    margin-bottom: 1rem;
}
.article.featured .art-tags {
    background-color: inherit !important;
    padding: 0 !important;
}
.section-featured .art-tags p,
.article .art-tags p {
    color: inherit;
    font-size: inherit;
    padding: 0 !important;
}
.section-featured .art-tags,
.section-featured .art-tags p a,
.article .art-tags,
.article .art-tags p a {
    color: #bbb;
    font-size: .85rem;
    text-decoration: inherit;
}
.section-featured .art-tags p a:hover,
.article .art-tags p a:hover {
    color: #666;
}
.home_468_60 {
    display: none;
}
.bottom-left {
    float: none;
    width: auto;
}
.yos-acasa-box1,
.yos-acasa-box2 {
    display: flex;
    flex-flow: row wrap;
    align-content: stretch;
}
.article.fixed,
.list-stiri-acasa {
    flex: 1 0 50%;
    float: none;
    margin: 0;
    top: auto;
    width: auto;
}
.list-stiri-acasa .article {
    padding: 1rem !important;
}
.yos-acasa-box1> .section,
.yos-acasa-box2 > .section {
}
.yos-acasa-box2 > .section {
    flex: 1 0 100%;
}

.list-stiri-desc1{
  width: auto;
  height: auto !important;
  padding: 0;
  float: left;
  overflow: visible;
}
.list-stiri-desc1 .article .art-title a {
    font-size: inherit !important;
}
.col-1 {
    float: none;
}
.col-1, .col-2 {
    width: auto;
    padding: 0;
}
  
.section .section-title a {
    /* font-family: 'Roboto Slab', serif; */
	font-family: inherit;
    color: #fff;
    text-decoration: none;
    white-space: nowrap;
}
.section .section-title a:hover {
    text-decoration: underline;
}
.yos-acasa-box2 > .section .section-title .toate_stirile {
    color: #fff;
    font-size: .9rem;
    font-weight: 300;
    white-space: nowrap;
    display: none;
}
.section .section-header .section-subscribe {
    display: none;
}


.section {
	display: flex;
    padding: 0;
