.textarea-comments,
.input-comments {
    border: 0;
    background: none;
    padding: .5rem;
    font-size: inherit;
    color: #fff;
    width: 100%;
}
.cform input,
.cform textarea {
    border: 0;
    background-color: rgba(0, 0, 0, .25);
    padding: .5rem;
    box-sizing: border-box;
    color: #fff;
    width: 100%;
}
.input-comments:focus,
.textarea-comments:focus {
    background: none;
    color: #fff;
}
.cform input:focus,
.cform textarea:focus {
    color: #fff;
    border: 0 !important;
    background-color: #666 !important;
}
::-webkit-input-placeholder {
    color: #ddd;
    font-size: inherit;
}
::-moz-placeholder {
    color: #ddd;
    font-size: inherit;
}
:-ms-input-placeholder {
    color: #ddd;
    font-size: inherit;
}
:-moz-placeholder {
    color: #ddd;
    font-size: inherit;
}
.captcha {
    font-size: inherit;
    color: #fff;
}
.captcha img {
    float: none;
    vertical-align: middle;
    margin-left: 1rem;
}
input.captcha_txt {
    height: auto;
    font-size: inherit;
    text-align: center;
    width: auto;
    vertical-align: middle;
}
.img-noi {
    color: #444;
    display: inline-block;
    float: none;
    width: 1.5rem;
    height: 1.5rem;
    margin: 0 0 0 1rem;
    text-indent: 0;
    vertical-align: middle;
}
.txt-obli {
    display: block;
    font-size: inherit;
    color: #FFEB3B;
    padding: 1rem;
    background-color: transparent;
    border: 1px dashed #FFEB3B;
    text-align: center;
    margin: 1rem 0;
}
.cform input[type="submit"] {
    background: none;
    width: auto;
    background-color: #b22222;
    /*#b22222;*/
    /*#258c8c*/
    padding: .75rem 1rem;
    height: auto;
}
.comments-form td[colspan="2"] {
    text-align: center;
}
.submit-comments {
    background: none;
    width: auto;
    height: auto;
    border: 0;
    cursor: pointer;
    font-size: inherit;
    color: #fff;
    font-weight: bold;
    text-align: center;
    line-height: normal;
    float: none;
    margin: 0 auto;
}
.fbcom {
    margin: 0 !important;
    padding: 1rem !important;
    border-radius: 0 !important;
    background-color: rgba(0, 0, 0, .03) !important;
    font-size: 1.5rem !important;
    font-weight: 900;
    width: auto !important;
    color: #444 !important;
    text-align: center;
    border-top: 1px solid rgba(0, 0, 0, .05);
    /*font-family: 'Roboto Slab', serif;*/
	font-family: 'Calibri', sans-serif;
	text-transform: uppercase;
}
.fbcom a {
    display: none;
}
.fb-comments {
    margin: 0 !important;
    padding: 1rem;
    text-align: center;
    width: 100%;
    box-sizing: border-box;
    background-color: rgba(0, 0, 0, .03);
}
.fb-comments > span{
    display: block;
}
ul.com-tabs {
    display: none;
    _display: flex;
    flex-flow: row nowrap;
    justify-content: center;
    align-items: center;
}
ul.com-tabs li {
    float: none;
}
ul.com-tabs a {
    float: none;
    display: block;
    margin: 0;
    width: auto;
    height: auto;
    line-height: normal;
    text-align: center;
    color: inherit;
    font-size: inherit;
    font-weight: bold;
    text-decoration: none;
    padding: 1rem;
}
ul.com-tabs a[name="mergi_la_comentariu"] {
    display: none;
}
.com-panes .com-pane {
    min-height: 10rem;
    padding: 1rem;
    background-color: inherit;
}
.com-comment {
    background: #f3f3f3;
    border-radius: 1px;
    width: auto;
    max-width: 40rem;
    padding: 1rem;
    margin: auto;
    margin-bottom: 1rem;
}
.com-comment > table {
    width: 100%;
}
.com-date {
    font-size: inherit;
    margin-bottom: 1rem;
    color: rgba(0, 0, 0, .33);
    text-transform: uppercase;
}
.com-thumb {
    padding: 0;
    margin: 0 1rem 1rem 0;
}
.com-name {
    font-size: inherit;
}
.com-text {
    font-size: inherit;
    font-weight: 400;
}
.com-vote {
    border-bottom: 1px solid rgba(0, 0, 0, .1);
    padding: 1rem;
}
.com-vote-left {
    float: none;
    display: inline-block;
}
.com-vote-right {
    float: none;
    display: inline-block;
    margin-left: 1rem;
}
.ras-opinie {
    margin: 0 0 0 3.5rem !important;
    padding: 1rem;
    border-left: 1px solid #ddd;
    box-sizing: border-box;
}
.ras-opinie table {
    width: 100%;
}
.ras-opinie .ras-nume {
    font-size: inherit;
    font-weight: bold;
}
.ras-opinie .ras-data {
    color: rgba(0, 0, 0, .25);
    font-size: .85rem;
    text-transform: uppercase;
}
.ras-opinie .d-descriere {
    font-size: inherit;
}
.com-reply {
    text-align: right;
    padding-top: 1rem;
}
.com-reply a {
    display: inline-block;
    background: none;
    width: auto;
    height: auto;
    float: none;
    line-height: normal;
    text-align: center;
    font-size: inherit;
    font-weight: normal;
    text-decoration: none;
    color: #fff;
    background-color: #444;
    padding: .75rem 1rem !important;
}
.com-reply a:hover {
    background-color: #555;
    color: #fff;
}




@media( max-width: 78rem ){
    .top .head-menu{
		flex-flow: row wrap;
	}
    .article.fixed, .list-stiri-acasa{
		flex: 1 0 100%;
	}
    .bot .bot-menu ul,
    .bot .bot-menu-center ul,
    .bot .links,
    .bot .links ul,
    .bot .mini-banner ul{
		flex-flow: row wrap;
	}
    .article.featured .art-thumb > img{
		width: 12rem;
	}
    .mid .wrap-left {
    	width: 66%;
	}
    .mid .wrap-right{
		width: 34%;
		flex: 0 0 34%;
    }
    .section .articol-detalii .articol-right{
		display: none;
	}
    
    .page_pos{
    	text-align: center;
	}
    .results{
		width: 100%;
		box-sizing: border-box;
    	text-align: center;
		margin-bottom: 1rem;
	}
    .pages{
		width: 100%;
		box-sizing: border-box;
	}
    .numbers{
		justify-content: center;
	}
}
@media( max-width: 48rem ){
    
    .numbers a,
    .numbers span{
		padding: .75rem;
	}
    .head-left{
		margin: 0 auto;
	}
    .mid > .center{
		display: block;
	}
    .mid .wrap-left {
    	width: 100%;
	}
    .mid .wrap-right {
		width: 100%;
	}
    .top .head-menu-right{
		flex-flow: row wrap;
	}
    .section .section-header .section-title{
        font-size: 1.7rem;
	}
}
@media( max-width: 36rem ){
    .top .head-menu{
		display: none;
	}
    .menu{
		display: none;
	}
    .numbers{
		flex-flow: row wrap;
		overflow: hidden;
    }
    .numbers a,
    .numbers span{
		padding: .5rem;
	}
}
